"use client";

import { useState, useRef, useEffect } from "react";
import { ChevronLeft, ChevronRight, BookOpen } from "lucide-react";
import { Button } from "react-bootstrap";

// Sample featured books data
const featuredBooks = [
  {
    id: 1,
    title: "The Great Gatsby",
    author: "F. Scott Fitzgerald",
    coverUrl: "/placeholder.svg?height=400&width=250",
    year: "1925",
    genre: "Classic Fiction",
    description:
      "A tale of wealth, love, and the American Dream set in the Roaring Twenties, following the mysterious millionaire Jay Gatsby.",
  },
  {
    id: 2,
    title: "To Kill a Mockingbird",
    author: "Harper Lee",
    coverUrl: "/placeholder.svg?height=400&width=250",
    year: "1960",
    genre: "Southern Gothic",
    description:
      "A story of racial injustice and moral growth in the American South during the Great Depression, seen through the eyes of a young girl.",
  },
  {
    id: 3,
    title: "1984",
    author: "George Orwell",
    coverUrl: "/placeholder.svg?height=400&width=250",
    year: "1949",
    genre: "Dystopian Fiction",
    description:
      "A chilling portrayal of a totalitarian future where critical thought is suppressed under a surveillance state.",
  },
  {
    id: 4,
    title: "Pride and Prejudice",
    author: "Jane Austen",
    coverUrl: "/placeholder.svg?height=400&width=250",
    year: "1813",
    genre: "Romance",
    description:
      "A romantic novel of manners that follows the character development of Elizabeth Bennet in Georgian England.",
  },
  {
    id: 5,
    title: "The Odyssey",
    author: "Homer",
    coverUrl: "/placeholder.svg?height=400&width=250",
    year: "8th Century BCE",
    genre: "Epic Poetry",
    description:
      "An ancient Greek epic poem that follows the hero Odysseus on his journey home after the Trojan War.",
  },
];

const FeaturedBooksCarousel = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const [hoveredBook, setHoveredBook] = useState(null);
  const [carouselWidth, setCarouselWidth] = useState(0);
  const carouselRef = useRef(null);

  useEffect(() => {
    const updateWidth = () => {
      if (carouselRef.current) {
        setCarouselWidth(carouselRef.current.offsetWidth);
      }
    };

    updateWidth();
    window.addEventListener("resize", updateWidth);
    return () => window.removeEventListener("resize", updateWidth);
  }, []);

  const nextSlide = () => {
    if (isTransitioning) return;
    setIsTransitioning(true);
    setActiveIndex((prev) =>
      prev === featuredBooks.length - 1 ? 0 : prev + 1
    );
    setTimeout(() => setIsTransitioning(false), 500);
  };

  const prevSlide = () => {
    if (isTransitioning) return;
    setIsTransitioning(true);
    setActiveIndex((prev) =>
      prev === 0 ? featuredBooks.length - 1 : prev - 1
    );
    setTimeout(() => setIsTransitioning(false), 500);
  };

  const goToSlide = (index) => {
    if (isTransitioning || index === activeIndex) return;
    setIsTransitioning(true);
    setActiveIndex(index);
    setTimeout(() => setIsTransitioning(false), 500);
  };

  // Calculate visible books based on carousel width
  const getVisibleBooks = () => {
    let visibleCount = 1;
    if (carouselWidth >= 768) visibleCount = 3;
    else if (carouselWidth >= 576) visibleCount = 2;

    const books = [];
    for (let i = 0; i < visibleCount; i++) {
      const index = (activeIndex + i) % featuredBooks.length;
      books.push({ ...featuredBooks[index], position: i });
    }
    return books;
  };

  const visibleBooks = getVisibleBooks();

  return (
    <div className="featured-books-section mb-5">
      <div className="section-header text-center mb-4">
        <h2
          className="font-heading mb-3"
          style={{
            color: "#cdaf73",
            fontWeight: "300",
            letterSpacing: "0.2em",
            fontSize: "2rem",
          }}
        >
          Literary Treasures
        </h2>
        <div className="decorative-line mx-auto mb-4"></div>
        <p
          className="mx-auto"
          style={{
            maxWidth: "600px",
            color: "#a8a090",
            letterSpacing: "0.05em",
            fontSize: "1rem",
            fontWeight: "300",
          }}
        >
          Discover timeless classics and contemporary masterpieces
        </p>
      </div>

      <div className="carousel-container position-relative" ref={carouselRef}>
        <div
          className="carousel-inner"
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            minHeight: "450px",
            padding: "2rem 0",
          }}
        >
          {visibleBooks.map((book, index) => (
            <div
              key={`${book.id}-${index}`}
              className={`carousel-item ${
                index === 1 && visibleBooks.length === 3 ? "center" : ""
              }`}
              style={{
                flex: "0 0 auto",
                width:
                  visibleBooks.length === 1
                    ? "100%"
                    : visibleBooks.length === 2
                    ? "50%"
                    : "33.333%",
                padding: "0 1rem",
                transition: "all 0.5s ease",
                transform:
                  visibleBooks.length === 3 && index === 1
                    ? "scale(1.1)"
                    : hoveredBook === book.id
                    ? "scale(1.05) translateY(-10px)"
                    : "scale(1)",
                zIndex:
                  hoveredBook === book.id
                    ? 10
                    : index === 1 && visibleBooks.length === 3
                    ? 5
                    : 1,
              }}
            >
              <div
                className="book-card"
                style={{
                  position: "relative",
                  border: "1px solid rgba(205, 175, 115, 0.2)",
                  background: "rgba(10, 31, 28, 0.5)",
                  height: "100%",
                  transition: "all 0.3s ease",
                  boxShadow:
                    hoveredBook === book.id
                      ? "0 15px 30px rgba(0,0,0,0.4), 0 0 15px rgba(205, 175, 115, 0.2)"
                      : "0 5px 15px rgba(0,0,0,0.2)",
                }}
                onMouseEnter={() => setHoveredBook(book.id)}
                onMouseLeave={() => setHoveredBook(null)}
              >
                <div
                  className="book-cover-wrapper"
                  style={{ position: "relative", overflow: "hidden" }}
                >
                  <div
                    className="book-cover"
                    style={{
                      position: "relative",
                      paddingTop: "150%", // Aspect ratio
                      overflow: "hidden",
                    }}
                  >
                    <img
                      src={book.coverUrl || "/placeholder.svg"}
                      alt={book.title}
                      style={{
                        position: "absolute",
                        top: 0,
                        left: 0,
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                        transition: "all 0.5s ease",
                        transform:
                          hoveredBook === book.id ? "scale(1.05)" : "scale(1)",
                      }}
                    />
                    <div
                      style={{
                        position: "absolute",
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        background:
                          "linear-gradient(to bottom, rgba(0,0,0,0) 70%, rgba(0,0,0,0.8) 100%)",
                      }}
                    ></div>
                  </div>

                  {/* Book info overlay on hover */}
                  <div
                    className="book-info-overlay"
                    style={{
                      position: "absolute",
                      top: 0,
                      left: 0,
                      right: 0,
                      bottom: 0,
                      background: "rgba(10, 31, 28, 0.85)",
                      display: "flex",
                      flexDirection: "column",
                      justifyContent: "center",
                      alignItems: "center",
                      padding: "1.5rem",
                      opacity: hoveredBook === book.id ? 1 : 0,
                      visibility:
                        hoveredBook === book.id ? "visible" : "hidden",
                      transition: "all 0.3s ease",
                    }}
                  >
                    <h4
                      className="font-heading text-center mb-2"
                      style={{
                        color: "#cdaf73",
                        fontWeight: "400",
                        letterSpacing: "0.1em",
                      }}
                    >
                      {book.title}
                    </h4>
                    <p
                      className="text-center mb-3"
                      style={{
                        color: "#a8a090",
                        letterSpacing: "0.05em",
                        fontSize: "0.9rem",
                      }}
                    >
                      by {book.author}
                    </p>
                    <div
                      className="decorative-line mx-auto my-3"
                      style={{ width: "50%" }}
                    ></div>
                    <p
                      className="text-center mb-3"
                      style={{
                        color: "#e9e2d0",
                        fontSize: "0.9rem",
                        lineHeight: "1.6",
                        maxHeight: "150px",
                        overflow: "hidden",
                      }}
                    >
                      {book.description}
                    </p>
                    <Button
                      variant="outline-light"
                      size="sm"
                      className="mt-2 px-3 py-2"
                      style={{
                        fontSize: "0.8rem",
                        borderColor: "rgba(205, 175, 115, 0.3)",
                        color: "#cdaf73",
                        borderRadius: "0",
                        letterSpacing: "0.1em",
                        fontWeight: "300",
                      }}
                    >
                      <BookOpen size={14} className="me-2" />
                      Explore
                    </Button>
                  </div>
                </div>

                <div className="book-info p-3">
                  <h5
                    className="font-heading text-center mb-1"
                    style={{
                      color: "#cdaf73",
                      fontWeight: "400",
                      letterSpacing: "0.1em",
                      fontSize: "1.1rem",
                    }}
                  >
                    {book.title}
                  </h5>
                  <p
                    className="text-center mb-0"
                    style={{
                      color: "#a8a090",
                      letterSpacing: "0.05em",
                      fontSize: "0.8rem",
                    }}
                  >
                    {book.author}
                  </p>
                </div>

                {/* Year badge */}
                <div
                  className="year-badge"
                  style={{
                    position: "absolute",
                    top: "10px",
                    right: "10px",
                    background: "rgba(10, 31, 28, 0.8)",
                    border: "1px solid rgba(205, 175, 115, 0.3)",
                    padding: "0.3rem 0.6rem",
                    fontSize: "0.7rem",
                    color: "#cdaf73",
                    letterSpacing: "0.05em",
                  }}
                >
                  {book.year}
                </div>

                {/* Genre badge */}
                <div
                  className="genre-badge"
                  style={{
                    position: "absolute",
                    bottom: "60px",
                    left: "0",
                    background: "rgba(205, 175, 115, 0.8)",
                    padding: "0.3rem 0.6rem",
                    fontSize: "0.7rem",
                    color: "#0a1f1c",
                    letterSpacing: "0.05em",
                    fontWeight: "500",
                    transform:
                      hoveredBook === book.id
                        ? "translateX(0)"
                        : "translateX(-100%)",
                    transition: "transform 0.3s ease",
                  }}
                >
                  {book.genre}
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Navigation arrows */}
        <Button
          variant="link"
          className="carousel-control prev"
          onClick={prevSlide}
          style={{
            position: "absolute",
            left: "10px",
            top: "50%",
            transform: "translateY(-50%)",
            color: "#cdaf73",
            background: "rgba(10, 31, 28, 0.5)",
            border: "1px solid rgba(205, 175, 115, 0.3)",
            width: "40px",
            height: "40px",
            borderRadius: "0",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 20,
          }}
        >
          <ChevronLeft size={20} />
        </Button>

        <Button
          variant="link"
          className="carousel-control next"
          onClick={nextSlide}
          style={{
            position: "absolute",
            right: "10px",
            top: "50%",
            transform: "translateY(-50%)",
            color: "#cdaf73",
            background: "rgba(10, 31, 28, 0.5)",
            border: "1px solid rgba(205, 175, 115, 0.3)",
            width: "40px",
            height: "40px",
            borderRadius: "0",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 20,
          }}
        >
          <ChevronRight size={20} />
        </Button>

        {/* Indicators */}
        <div
          className="carousel-indicators"
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "1.5rem",
          }}
        >
          {featuredBooks.map((_, index) => (
            <button
              key={index}
              type="button"
              style={{
                width: "8px",
                height: "8px",
                margin: "0 5px",
                background:
                  index === activeIndex
                    ? "#cdaf73"
                    : "rgba(205, 175, 115, 0.3)",
                border: "none",
                padding: 0,
                cursor: "pointer",
                transition: "all 0.3s ease",
              }}
              onClick={() => goToSlide(index)}
              aria-label={`Go to slide ${index + 1}`}
            ></button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default FeaturedBooksCarousel;
