    cards: false,
    cta: false,
  });

  // Intersection Observer for scroll animations
  useEffect(() => {
    const observerOptions = {
      threshold: 0.2,
      rootMargin: "0px 0px -100px 0px",
    };

    const observerCallback = (entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          setIsVisible((prev) => ({
            ...prev,
            [entry.target.dataset.section]: true,
          }));
        }
      });
    };

    const observer = new IntersectionObserver(
      observerCallback,
      observerOptions
    );

    const sections = document.querySelectorAll("[data-section]");
    sections.forEach((section) => observer.observe(section));

    return () => {
      sections.forEach((section) => observer.unobserve(section));
    };
  }, []);

  return (
    <div
      style={{
        backgroundColor: "#0a1f1c",
        color: "#e9e2d0",
        minHeight: "100vh",
        position: "relative",
        overflow: "hidden",
      }}
    >
      {/* Background pattern overlay */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundImage:
            "url(\"data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fillRule='evenodd'%3E%3Cg fill='%23cdaf73' fillOpacity='0.03'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E\")",
          opacity: 0.5,
          zIndex: 0,
        }}
      ></div>

      {/* Hero Section */}
      <div
        className="py-5 position-relative"
        data-section="hero"
        style={{
          opacity: isVisible.hero ? 1 : 0,
          transform: isVisible.hero ? "translateY(0)" : "translateY(20px)",
          transition: "opacity 0.8s ease, transform 0.8s ease",
        }}
      >
        <img
          src="/celestial-sun.png"
          alt=""
          className="celestial-decoration top-right"
          style={{
            opacity: 0.15,
            position: "absolute",
            top: "50px",
            right: "50px",
            width: "150px",
            height: "150px",
            animation: "pulse 8s infinite ease-in-out",
          }}
        />

        <Container className="py-5">
          <Row className="justify-content-center">
            <Col lg={8} className="text-center">
              <h1
                className="font-heading mb-4"
                style={{
                  color: "#cdaf73",
                  fontWeight: "300",
                  letterSpacing: "0.2em",
                  fontSize: "2.5rem",
                  position: "relative",
                  display: "inline-block",
                }}
              >
                About Our Vision
                <span
                  style={{
                    position: "absolute",
                    bottom: "-10px",
                    left: "50%",
                    transform: "translateX(-50%)",
                    width: "60px",
                    height: "1px",
                    background: "rgba(205, 175, 115, 0.5)",
                  }}
                ></span>
              </h1>
              <div className="decorative-line mx-auto mb-4"></div>
              <p
                className="mx-auto mb-5"
                style={{
                  maxWidth: "600px",
                  color: "#a8a090",
                  letterSpacing: "0.05em",
                  fontSize: "1.1rem",
                  lineHeight: "1.8",
                  fontWeight: "300",
                }}
              >
                Discover the essence of literature through our AI-powered book
                summaries and insights
              </p>
            </Col>
          </Row>
        </Container>
      </div>

      {/* Main Content */}
      <Container className="py-5">
        <Row
          className="mb-5 align-items-center"
          data-section="journey"
          style={{
            opacity: isVisible.journey ? 1 : 0,
            transform: isVisible.journey ? "translateY(0)" : "translateY(30px)",
            transition: "opacity 0.8s ease 0.2s, transform 0.8s ease 0.2s",
          }}
        >
          <Col lg={5} className="mb-5 mb-lg-0">
            <div
              className="arch-frame"
              style={{
                position: "relative",
                transition:
                  "transform 0.5s ease-in-out, box-shadow 0.5s ease-in-out",
                cursor: "pointer",
                overflow: "hidden",
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = "scale(1.02)";
                e.currentTarget.style.boxShadow =
                  "0 15px 30px rgba(0, 0, 0, 0.3), 0 0 15px rgba(205, 175, 115, 0.2)";
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = "scale(1)";
                e.currentTarget.style.boxShadow = "none";
              }}
            >
              <img
                src="/placeholder.svg?height=600&width=400"
                alt="About Literary Insights"
                className="img-fluid"
                style={{
                  transition: "transform 0.8s ease",
                }}
              />
              <div
                style={{
                  position: "absolute",
                  top: 0,
                  left: 0,
                  right: 0,
                  bottom: 0,
                  background:
                    "radial-gradient(ellipse at center, rgba(10, 31, 28, 0) 0%, rgba(10, 31, 28, 0.6) 100%)",
                  pointerEvents: "none",
                }}
              ></div>
            </div>
          </Col>
          <Col lg={7}>
            <div
              style={{
                position: "relative",
                padding: "2rem",
                background: "rgba(10, 31, 28, 0.3)",
                border: "1px solid rgba(205, 175, 115, 0.1)",
              }}
            >
              <h2
                className="font-heading mb-4"
                style={{
                  color: "#cdaf73",
                  fontWeight: "300",
                  letterSpacing: "0.15em",
                  fontSize: "2rem",
                }}
              >
                Our Literary Journey
              </h2>
              <div className="decorative-line mb-4"></div>
              <div
                style={{
                  fontFamily: "var(--font-lora)",
                  color: "#e9e2d0",
                  letterSpacing: "0.02em",
                  lineHeight: "1.8",
                }}
              >
                <p>
                  Welcome to our AI book summary app, designed to empower book
                  lovers, book clubs, researchers, and literature enthusiasts.
                  Our mission is to provide a valuable resource for discovering
                  and exploring new titles across a wide range of genres and
                  topics.
                </p>

                <p style={{ color: "#cdaf73", fontWeight: "500" }}>
                  One of the standout features of our app is the Q&A section,
                  where users can ask questions about the books and receive
                  answers from our AI assistant. This feature is designed to
                  provide additional insights, clarification, and in-depth
                  exploration of the literary works, further enhancing the
                  user's understanding and engagement.
                </p>

                <p>
                  Join us on this exciting journey as we redefine the way you
                  discover and engage with books. Welcome to our book summary
                  app – your gateway to a world of literary exploration,
                  discovery, and knowledge-sharing.
                </p>
              </div>

              {/* Decorative quill icon */}
              <div
                style={{
                  position: "absolute",
                  bottom: "20px",
                  right: "20px",
                  opacity: 0.1,
                }}
              >
                <Feather size={80} color="#cdaf73" />
              </div>
            </div>
          </Col>
        </Row>

        <div
          className="decorative-line my-5"
          style={{
            width: "100%",
            maxWidth: "800px",
            margin: "3rem auto",
          }}
        ></div>

        <Row
          className="justify-content-center text-center mb-5"
          data-section="philosophy"
          style={{
            opacity: isVisible.philosophy ? 1 : 0,
            transform: isVisible.philosophy
              ? "translateY(0)"
              : "translateY(30px)",
            transition: "opacity 0.8s ease 0.3s, transform 0.8s ease 0.3s",
          }}
        >
          <Col lg={8}>
            <h2
              className="font-heading mb-4"
              style={{
                color: "#cdaf73",
                fontWeight: "300",
                letterSpacing: "0.15em",
                fontSize: "2rem",
              }}
            >
              Our Philosophy
            </h2>
            <p
              className="mx-auto"
              style={{
                maxWidth: "700px",
                color: "#a8a090",
                letterSpacing: "0.05em",
                fontSize: "1.1rem",
                lineHeight: "1.8",
                fontWeight: "300",
              }}
            >
              We believe that literature is a gateway to understanding the human
              experience. Our AI-powered platform is designed to make the wisdom
              of books more accessible, allowing readers to explore new ideas
              and perspectives with ease and depth.
            </p>
          </Col>
        </Row>

        <Row
          className="g-4"
          data-section="cards"
          style={{
            opacity: isVisible.cards ? 1 : 0,
            transform: isVisible.cards ? "translateY(0)" : "translateY(30px)",
            transition: "opacity 0.8s ease 0.4s, transform 0.8s ease 0.4s",
          }}
        >
          {[
            {
              title: "Energy Healing",
              description:
                "Our summaries capture the essence and energy of each book, distilling complex ideas into accessible insights that resonate with readers.",
              icon: <Sparkles size={24} />,
            },
            {
              title: "Spiritual Activation",
              description:
                "Books have the power to awaken new perspectives and transform thinking. Our platform helps activate these insights through thoughtful analysis.",
              icon: <BookOpen size={24} />,
            },
            {
              title: "Intuitive Guidance",
              description:
                "Our AI assistant provides intuitive guidance through books, helping you discover connections and meanings that might otherwise remain hidden.",
              icon: <Compass size={24} />,
            },
          ].map((card, index) => (
            <Col md={4} key={index}>
              <div
                className="p-4 h-100"
                style={{
                  border: "1px solid rgba(205, 175, 115, 0.2)",
                  background: "rgba(10, 31, 28, 0.5)",
                  transition: "transform 0.3s ease, box-shadow 0.3s ease",
                  position: "relative",
                  overflow: "hidden",
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = "translateY(-5px)";
                  e.currentTarget.style.boxShadow =
                    "0 10px 30px rgba(0,0,0,0.2), 0 0 15px rgba(205, 175, 115, 0.1)";
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = "translateY(0)";
                  e.currentTarget.style.boxShadow = "none";
                }}
              >
                <div
                  style={{
                    position: "absolute",
                    top: "-20px",
                    right: "-20px",
                    width: "100px",
                    height: "100px",
                    background:
                      "radial-gradient(circle, rgba(205, 175, 115, 0.1) 0%, rgba(10, 31, 28, 0) 70%)",
                    borderRadius: "50%",
                  }}
                ></div>

                <div
                  style={{
                    display: "flex",
                    alignItems: "center",
                    marginBottom: "1rem",
                    color: "#cdaf73",
                  }}
                >
                  {card.icon}
                  <h3
                    className="font-heading mb-0 ms-2"
                    style={{
                      color: "#cdaf73",
                      fontWeight: "300",
                      letterSpacing: "0.1em",
                      fontSize: "1.5rem",
                    }}
                  >
                    {card.title}
                  </h3>
                </div>

                <div className="decorative-line mb-3"></div>
                <p
                  style={{
                    color: "#e9e2d0",
                    letterSpacing: "0.02em",
                    lineHeight: "1.8",
                  }}
                >
                  {card.description}
                </p>
              </div>
            </Col>
          ))}
        </Row>
      </Container>

      <div
        className="py-5 position-relative"
        data-section="cta"
        style={{
          opacity: isVisible.cta ? 1 : 0,
          transform: isVisible.cta ? "translateY(0)" : "translateY(30px)",
          transition: "opacity 0.8s ease 0.5s, transform 0.8s ease 0.5s",
        }}
      >
        <img
          src="/celestial-moon.png"
          alt=""
          className="celestial-decoration bottom-left"
          style={{
            opacity: 0.15,
            position: "absolute",
            bottom: "50px",
            left: "50px",
            width: "150px",
            height: "150px",
            animation: "pulse 8s infinite ease-in-out",
          }}
        />

        <Container className="py-5">
          <Row className="justify-content-center">
            <Col lg={8} className="text-center">
              <h2
                className="font-heading mb-4"
                style={{
                  color: "#cdaf73",
                  fontWeight: "300",
                  letterSpacing: "0.15em",
                  fontSize: "2rem",
                }}
              >
                Begin Your Literary Journey
              </h2>
              <div className="decorative-line mx-auto mb-4"></div>
              <p
                className="mx-auto mb-5"
                style={{
                  maxWidth: "600px",
                  color: "#a8a090",
                  letterSpacing: "0.05em",
                  fontSize: "1.1rem",
                  lineHeight: "1.8",
                  fontWeight: "300",
                }}
              >
                Explore the depths of literature with our AI-powered insights
                and discover new dimensions in your favorite books
              </p>
              <a
                href="/"
                className="btn px-4 py-2"
                style={{
                  backgroundColor: "transparent",
                  border: "1px solid rgba(205, 175, 115, 0.3)",
                  color: "#cdaf73",
                  borderRadius: "0",
                  letterSpacing: "0.1em",
                  fontWeight: "300",
                  transition: "all 0.3s ease",
                  position: "relative",
                  overflow: "hidden",
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.backgroundColor =
                    "rgba(205, 175, 115, 0.1)";
                  e.currentTarget.style.borderColor =
                    "rgba(205, 175, 115, 0.5)";
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.backgroundColor = "transparent";
                  e.currentTarget.style.borderColor =
                    "rgba(205, 175, 115, 0.3)";
                }}
              >
                Explore Books
              </a>
            </Col>
          </Row>
        </Container>
      </div>

      {/* Add CSS for animations */}
      <style jsx global>{`
        @keyframes pulse {
          0% {
            opacity: 0.1;
          }
          50% {
            opacity: 0.2;
          }
          100% {
            opacity: 0.1;
          }
        }

        .arch-frame::before {
          content: "";
          position: absolute;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          border: 1px solid rgba(205, 175, 115, 0.3);
          z-index: 1;
          pointer-events: none;
        }

        .arch-frame::after {
          content: "";
          position: absolute;
          top: -10%;
          left: -10%;
          right: -10%;
          bottom: -10%;
          background: radial-gradient(
            ellipse at center,
            rgba(10, 31, 28, 0) 0%,
            rgba(10, 31, 28, 0.8) 100%
          );
          pointer-events: none;
          z-index: 2;
        }

        .decorative-line {
          height: 1px;
          background: linear-gradient(
            90deg,
            rgba(205, 175, 115, 0),
            rgba(205, 175, 115, 0.5),
            rgba(205, 175, 115, 0)
          );
          width: 100%;
          position: relative;
        }

        .decorative-line::before {
          content: "✦";
          position: absolute;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
          color: #cdaf73;
          background-color: #0a1f1c;
          padding: 0 10px;
          font-size: 0.8rem;
        }
      `}</style>
    </div>
  );
};

export default About;
