sourcesContent:
"use client";

import { useState, useRef, useEffect } from "react";
import axios from "axios";
import { TypeAnimation } from "react-type-animation";
import { RefreshCw, Search, BarChart2, BookOpen, Clock } from "lucide-react";
import { Button, Form, Container, Row, Col, Spinner } from "react-bootstrap";
import FeaturedBooksCarousel from "./components/featured-books-carousel";
import BookTrendsVisualization from "./components/book-trends-visualization";

// Sample data for visualizations
const sampleGenreData = [
  { name: "Fiction", value: 45 },
  { name: "Non-Fiction", value: 30 },
  { name: "Mystery", value: 15 },
  { name: "Science Fiction", value: 10 },
];

const sampleTimelineData = [
  { year: "1800", count: 5 },
  { year: "1850", count: 12 },
  { year: "1900", count: 25 },
  { year: "1950", count: 40 },
  { year: "2000", count: 65 },
  { year: "Present", count: 80 },
];

function Book() {
  const [bookName, setBookName] = useState("");
  const [bookInfo, setBookInfo] = useState("");
  const [bookUrl, setBookUrl] = useState("");
  const [animationKey, setAnimationKey] = useState(0);
  const [questions, setQuestions] = useState({});
  const [showQuestion, setShowQuestion] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [bookMeta, setBookMeta] = useState({ title: "", author: "" });
  const [activeTab, setActiveTab] = useState("summary");
  const [bookAngle, setBookAngle] = useState(0);
  const [isFlipped, setIsFlipped] = useState(false);
  const [hoverStates, setHoverStates] = useState({});
  const [selectedGenre, setSelectedGenre] = useState(null);

  const bookCoverRef = useRef(null);
  const chartRef = useRef(null);

  // Handle mouse movement over book cover for 3D effect
  const handleMouseMove = (e) => {
    if (!bookCoverRef.current) return;

    const rect = bookCoverRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const centerX = rect.width / 2;
    const centerY = rect.height / 2;

    const angleX = (y - centerY) / 20;
    const angleY = (centerX - x) / 20;

    setBookAngle({ x: angleX, y: angleY });
  };

  const resetBookAngle = () => {
    setBookAngle({ x: 0, y: 0 });
  };

  // Draw genre chart
  useEffect(() => {
    if (chartRef.current && activeTab === "trends") {
      const ctx = chartRef.current.getContext("2d");
      const centerX = chartRef.current.width / 2;
      const centerY = chartRef.current.height / 2;
      const radius = Math.min(centerX, centerY) - 20;

      // Clear canvas
      ctx.clearRect(0, 0, chartRef.current.width, chartRef.current.height);

      // Draw chart background
      ctx.beginPath();
      ctx.arc(centerX, centerY, radius, 0, 2 * Math.PI);
      ctx.fillStyle = "rgba(10, 31, 28, 0.5)";
      ctx.fill();
      ctx.strokeStyle = "rgba(205, 175, 115, 0.3)";
      ctx.lineWidth = 1;
      ctx.stroke();

      // Draw segments
      let startAngle = 0;
      sampleGenreData.forEach((genre, index) => {
        const sliceAngle = (genre.value / 100) * 2 * Math.PI;

        // Draw slice
        ctx.beginPath();
        ctx.moveTo(centerX, centerY);
        ctx.arc(centerX, centerY, radius, startAngle, startAngle + sliceAngle);
        ctx.closePath();

        // Determine if this segment is selected
        const isSelected = selectedGenre === index;

        // Fill with gradient
        const gradient = ctx.createRadialGradient(
          centerX,
          centerY,
          0,
          centerX,
          centerY,
          radius
        );

        if (isSelected) {
          gradient.addColorStop(0, "rgba(205, 175, 115, 0.8)");
          gradient.addColorStop(1, "rgba(205, 175, 115, 0.4)");
        } else {
          gradient.addColorStop(0, `rgba(205, 175, 115, ${0.3 + index * 0.1})`);
          gradient.addColorStop(
            1,
            `rgba(205, 175, 115, ${0.1 + index * 0.05})`
          );
        }

        ctx.fillStyle = gradient;
        ctx.fill();
        ctx.strokeStyle = "rgba(205, 175, 115, 0.5)";
        ctx.lineWidth = 1;
        ctx.stroke();

        // Add label
        const labelAngle = startAngle + sliceAngle / 2;
        const labelRadius = radius * 0.7;
        const labelX = centerX + labelRadius * Math.cos(labelAngle);
        const labelY = centerY + labelRadius * Math.sin(labelAngle);

        ctx.font = isSelected
          ? "bold 12px var(--font-cormorant)"
          : "12px var(--font-cormorant)";
        ctx.fillStyle = "#cdaf73";
        ctx.textAlign = "center";
        ctx.textBaseline = "middle";
        ctx.fillText(genre.name, labelX, labelY);

        // Add value
        const valueRadius = radius * 0.85;
        const valueX = centerX + valueRadius * Math.cos(labelAngle);
        const valueY = centerY + valueRadius * Math.sin(labelAngle);

        ctx.font = "10px var(--font-lora)";
        ctx.fillStyle = "#a8a090";
        ctx.fillText(`${genre.value}%`, valueX, valueY);

        startAngle += sliceAngle;
      });

      // Draw center circle
      ctx.beginPath();
      ctx.arc(centerX, centerY, radius * 0.3, 0, 2 * Math.PI);
      ctx.fillStyle = "rgba(10, 31, 28, 0.8)";
      ctx.fill();
      ctx.strokeStyle = "rgba(205, 175, 115, 0.5)";
      ctx.lineWidth = 1;
      ctx.stroke();

      // Add center text
      ctx.font = "14px var(--font-cormorant)";
      ctx.fillStyle = "#cdaf73";
      ctx.textAlign = "center";
      ctx.textBaseline = "middle";
      ctx.fillText("Genre", centerX, centerY - 8);
      ctx.fillText("Distribution", centerX, centerY + 8);
    }
  }, [activeTab, selectedGenre]);

  // Draw timeline chart
  useEffect(() => {
    if (chartRef.current && activeTab === "timeline") {
      const ctx = chartRef.current.getContext("2d");
      const width = chartRef.current.width;
      const height = chartRef.current.height;
      const padding = 40;

      // Clear canvas
      ctx.clearRect(0, 0, width, height);

      // Draw background
      ctx.fillStyle = "rgba(10, 31, 28, 0.5)";
      ctx.fillRect(0, 0, width, height);

      // Draw timeline line
      ctx.beginPath();
      ctx.moveTo(padding, height / 2);
      ctx.lineTo(width - padding, height / 2);
      ctx.strokeStyle = "rgba(205, 175, 115, 0.5)";
      ctx.lineWidth = 1;
      ctx.stroke();

      // Draw data points
      const dataWidth = width - padding * 2;
      const pointSpacing = dataWidth / (sampleTimelineData.length - 1);

      sampleTimelineData.forEach((point, index) => {
        const x = padding + index * pointSpacing;
        const normalizedValue = point.count / 100;
        const y = height - normalizedValue * (height - padding * 2) - padding;

        // Draw vertical line
        ctx.beginPath();
        ctx.moveTo(x, height / 2);
        ctx.lineTo(x, y);
        ctx.strokeStyle = "rgba(205, 175, 115, 0.3)";
        ctx.lineWidth = 1;
        ctx.stroke();

        // Draw point
        ctx.beginPath();
        ctx.arc(x, y, 6, 0, 2 * Math.PI);
        ctx.fillStyle = "rgba(205, 175, 115, 0.8)";
        ctx.fill();
        ctx.strokeStyle = "#0a1f1c";
        ctx.lineWidth = 1;
        ctx.stroke();

        // Draw year label
        ctx.font = "12px var(--font-cormorant)";
        ctx.fillStyle = "#cdaf73";
        ctx.textAlign = "center";
        ctx.textBaseline = "middle";
        ctx.fillText(point.year, x, height / 2 + 20);

        // Draw count label
        ctx.font = "10px var(--font-lora)";
        ctx.fillStyle = "#a8a090";
        ctx.fillText(point.count.toString(), x, y - 15);
      });

      // Draw title
      ctx.font = "16px var(--font-cormorant)";
      ctx.fillStyle = "#cdaf73";
      ctx.textAlign = "center";
      ctx.textBaseline = "top";
      ctx.fillText("Literary Timeline", width / 2, 15);
    }
  }, [activeTab]);

  const qCalled = async (qKey) => {
    try {
      setIsLoading(true);
      setError("");

      const questionRes = await axios.get(`http://127.0.0.1:5000/question/`);
      const questionText = questionRes.data.q;

      const ansRes = await axios.post(`http://127.0.0.1:5000/questions/`, {
        q: questionText,
      });

      setQuestions((prev) => ({ ...prev, [qKey]: questionText }));
      setBookInfo(ansRes.data.data);
      setShowQuestion(questionText);
      setAnimationKey((prev) => prev + 1);
      setActiveTab("summary");
    } catch (error) {
      console.error("Error fetching question or answer:", error);
      setError("Failed to fetch question or answer. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!bookName.trim()) return;

    try {
      setIsLoading(true);
      setError("");
      setBookInfo("");
      setBookUrl("");

      const response = await axios.get(
        `http://127.0.0.1:5000/info/${bookName}`
      );
      const bookmeta = await axios.get(`http://127.0.0.1:5000/bookinfo`);
      setBookMeta(bookmeta.data);

      const cover_img = await axios.get(
        `https://bookcover.longitood.com/bookcover`,
        {
          params: {
            book_title: bookmeta.data.title,
            author_name: bookmeta.data.author,
          },
        }
      );

      const questionData = await axios.get(`http://127.0.0.1:5000/questions/`);

      // Summarize the full text
      const summaryRes = await axios.post(`http://127.0.0.1:5000/summarize`, {
        text: response.data.data,
      });

      setQuestions(questionData.data);
      setBookUrl(cover_img.data.url);
      setBookInfo(summaryRes.data.summary);
      setShowQuestion("");
      setAnimationKey((prev) => prev + 1);
      setActiveTab("summary");
    } catch (error) {
      console.error("Error fetching book data:", error);
      setError(
        "Failed to fetch book information. Please check the book name and try again."
      );
    } finally {
      setIsLoading(false);
    }
  };

  const handleResummarize = async () => {
    try {
      setIsLoading(true);
      setError("");

      const res = await axios.post(`http://127.0.0.1:5000/summarize`, {
        text: bookInfo,
      });
      setBookInfo(res.data.summary);
      setShowQuestion("Re-summarized:");
      setAnimationKey((prev) => prev + 1);
    } catch (err) {
      console.error("Error re-summarizing:", err);
      setError("Failed to re-summarize. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleHover = (id, isHovered) => {
    setHoverStates((prev) => ({
      ...prev,
      [id]: isHovered,
    }));
  };

  return (
    <div
      className="book-page py-5"
      style={{
        backgroundColor: "#0a1f1c",
        minHeight: "100vh",
        color: "#e9e2d0",
      }}
    >
      <Container>
        <div className="text-center mb-5 fade-in">
          <h1
            className="font-heading mb-3"
            style={{
              color: "#cdaf73",
              fontWeight: "300",
              letterSpacing: "0.2em",
              fontSize: "2.5rem",
            }}
          >
            Discover Literary Wisdom
          </h1>
          <div className="decorative-line mx-auto mb-4"></div>
          <p
            className="mx-auto"
            style={{
              maxWidth: "600px",
              color: "#a8a090",
              letterSpacing: "0.05em",
              fontSize: "1.1rem",
              fontWeight: "300",
            }}
          >
            Explore the essence of literature through AI-powered summaries and
            insights
          </p>
        </div>

        {/* Add Featured Books Carousel */}
        <FeaturedBooksCarousel />

        {/* Add Book Trends Visualization */}
        <BookTrendsVisualization />

        {/* Rest of your existing component remains the same */}
        <Row className="justify-content-center mb-5">
          <Col md={8} lg={6}>
            <div
              className="search-container p-4 fade-in"
              style={{
                border: "1px solid rgba(205, 175, 115, 0.2)",
                borderRadius: "0",
                background: "rgba(10, 31, 28, 0.5)",
                transition: "all 0.3s ease",
              }}
              onMouseEnter={() => handleHover("search", true)}
              onMouseLeave={() => handleHover("search", false)}
            >
              <Form onSubmit={handleSubmit}>
                <div className="position-relative">
                  <Form.Control
                    type="text"
                    value={bookName}
                    onChange={(event) => setBookName(event.target.value)}
                    placeholder="Enter a book title..."
                    className="py-3 ps-4 pe-4 border-0"
                    style={{
                      backgroundColor: "rgba(0,0,0,0.2)",
                      color: "#e9e2d0",
                      borderRadius: "0",
                      fontSize: "1rem",
                      letterSpacing: "0.05em",
                      fontFamily: "var(--font-lora)",
                      borderLeft: hoverStates["search"]
                        ? "3px solid #cdaf73"
                        : "3px solid transparent",
                      transition: "all 0.3s ease",
                    }}
                  />
                  <Button
                    type="submit"
                    className="position-absolute d-flex align-items-center justify-content-center"
                    style={{
                      right: "10px",
                      top: "50%",
                      transform: "translateY(-50%)",
                      backgroundColor: "transparent",
                      border: "none",
                      color: "#cdaf73",
                    }}
                    disabled={isLoading || !bookName.trim()}
                  >
                    {isLoading ? (
                      <Spinner size="sm" animation="border" />
                    ) : (
                      <Search size={18} />
                    )}
                  </Button>
                </div>
              </Form>
            </div>
          </Col>
        </Row>

        {error && (
          <Row className="justify-content-center mb-4">
            <Col md={10}>
              <div
                className="p-3 fade-in"
                style={{
                  backgroundColor: "rgba(169, 68, 66, 0.1)",
                  color: "#e9a79c",
                  border: "1px solid rgba(169, 68, 66, 0.2)",
                }}
              >
                {error}
              </div>
            </Col>
          </Row>
        )}

        {bookUrl && bookInfo && (
          <Row>
            <Col md={4} lg={3} className="mb-4">
              <div
                className="book-container fade-in"
                style={{
                  border: "1px solid rgba(205, 175, 115, 0.2)",
                  padding: "1.5rem",
                  background: "rgba(10, 31, 28, 0.5)",
                }}
              >
                <div
                  className="book-cover-container mb-4"
                  style={{
                    position: "relative",
                    overflow: "hidden",
                    maxWidth: "200px",
                    margin: "0 auto",
                    perspective: "1000px",
                  }}
                  ref={bookCoverRef}
                  onMouseMove={handleMouseMove}
                  onMouseLeave={resetBookAngle}
                  onClick={() => setIsFlipped(!isFlipped)}
                >
                  <div
                    className="book-cover"
                    style={{
                      position: "relative",
                      width: "100%",
                      paddingTop: "150%",
                      transformStyle: "preserve-3d",
                      transform: isFlipped
                        ? "rotateY(180deg)"
                        : `rotateY(${bookAngle.y}deg) rotateX(${bookAngle.x}deg)`,
                      transition: "transform 0.6s ease",
                    }}
                  >
                    {/* Front Cover */}
                    <div
                      className="book-face front"
                      style={{
                        position: "absolute",
                        top: 0,
                        left: 0,
                        width: "100%",
                        height: "100%",
                        backfaceVisibility: "hidden",
                        border: "1px solid rgba(205, 175, 115, 0.3)",
                        boxShadow: hoverStates["book"]
                          ? "0 10px 30px rgba(0,0,0,0.5), 0 0 10px rgba(205, 175, 115, 0.3)"
                          : "0 5px 15px rgba(0,0,0,0.3)",
                        transition: "all 0.3s ease",
                      }}
                      onMouseEnter={() => handleHover("book", true)}
                      onMouseLeave={() => handleHover("book", false)}
                    >
                      <img
                        src={bookUrl || "/placeholder.svg"}
                        alt={bookMeta.title}
                        style={{
                          width: "100%",
                          height: "100%",
                          objectFit: "cover",
                        }}
                      />
                      <div
                        style={{
                          position: "absolute",
                          top: 0,
                          left: 0,
                          right: 0,
                          bottom: 0,
                          background:
                            "linear-gradient(to bottom, rgba(0,0,0,0) 70%, rgba(0,0,0,0.8) 100%)",
                        }}
                      ></div>
                    </div>

                    {/* Back Cover (Book Details) */}
                    <div
                      className="book-face back"
                      style={{
                        position: "absolute",
                        top: 0,
                        left: 0,
                        width: "100%",
                        height: "100%",
                        backfaceVisibility: "hidden",
                        transform: "rotateY(180deg)",
                        backgroundColor: "#0a1f1c",
                        border: "1px solid rgba(205, 175, 115, 0.3)",
                        padding: "1rem",
                        display: "flex",
                        flexDirection: "column",
                        justifyContent: "center",
                        alignItems: "center",
                      }}
                    >
                      <h5
                        style={{
                          color: "#cdaf73",
                          fontFamily: "var(--font-cormorant)",
                          fontWeight: "400",
                          letterSpacing: "0.1em",
                          marginBottom: "1rem",
                          textAlign: "center",
                        }}
                      >
                        Book Details
                      </h5>
                      <div
                        className="decorative-line mb-3"
                        style={{ width: "80%" }}
                      ></div>
                      <p
                        style={{
                          color: "#a8a090",
                          fontSize: "0.9rem",
                          marginBottom: "0.5rem",
                        }}
                      >
                        <strong>Title:</strong> {bookMeta.title}
                      </p>
                      <p
                        style={{
                          color: "#a8a090",
                          fontSize: "0.9rem",
                          marginBottom: "0.5rem",
                        }}
                      >
                        <strong>Author:</strong> {bookMeta.author}
                      </p>
                      <p
                        style={{
                          color: "#a8a090",
                          fontSize: "0.9rem",
                          marginBottom: "0.5rem",
                        }}
                      >
                        <strong>Genre:</strong> Fiction
                      </p>
                      <p style={{ color: "#a8a090", fontSize: "0.9rem" }}>
                        <strong>Published:</strong> 2023
                      </p>
                    </div>
                  </div>

                  <div
                    className="book-interaction-hint"
                    style={{
                      position: "absolute",
                      bottom: "10px",
                      left: "0",
                      right: "0",
                      textAlign: "center",
                      color: "#cdaf73",
                      fontSize: "0.8rem",
                      opacity: hoverStates["book"] ? "1" : "0",
                      transition: "opacity 0.3s ease",
                    }}
                  >
                    Click to flip
                  </div>
                </div>

                <h4
                  className="font-heading text-center mb-2"
                  style={{
                    color: "#cdaf73",
                    fontWeight: "400",
                    letterSpacing: "0.1em",
                  }}
                >
                  {bookMeta.title}
                </h4>

                {bookMeta.author && (
                  <p
                    className="text-center mb-4"
                    style={{
                      color: "#a8a090",
                      letterSpacing: "0.05em",
                      fontSize: "0.9rem",
                    }}
                  >
                    by {bookMeta.author}
                  </p>
                )}

                <div className="decorative-line mx-auto my-4"></div>

                <div className="d-flex justify-content-center">
                  <Button
                    variant="outline-light"
                    size="sm"
                    className="mx-2 px-3 py-2 book-action-btn"
                    style={{
                      fontSize: "0.8rem",
                      borderColor: "rgba(205, 175, 115, 0.3)",
                      color: "#cdaf73",
                      borderRadius: "0",
                      letterSpacing: "0.1em",
                      fontWeight: "300",
                      transition: "all 0.3s ease",
                    }}
                    onMouseEnter={() => handleHover("library-btn", true)}
                    onMouseLeave={() => handleHover("library-btn", false)}
                  >
                    Add to Library
                  </Button>
                </div>
              </div>
            </Col>

            <Col md={8} lg={9}>
              <div
                className="content-tabs mb-4 fade-in"
                style={{
                  display: "flex",
                  borderBottom: "1px solid rgba(205, 175, 115, 0.2)",
                }}
              >
                <div
                  className={`tab-item ${
                    activeTab === "summary" ? "active" : ""
                  }`}
                  onClick={() => setActiveTab("summary")}
                  style={{
                    padding: "1rem 1.5rem",
                    cursor: "pointer",
                    color: activeTab === "summary" ? "#cdaf73" : "#a8a090",
                    borderBottom:
                      activeTab === "summary" ? "2px solid #cdaf73" : "none",
                    transition: "all 0.3s ease",
                    display: "flex",
                    alignItems: "center",
                    gap: "0.5rem",
                  }}
                  onMouseEnter={() => handleHover("tab-summary", true)}
                  onMouseLeave={() => handleHover("tab-summary", false)}
                >
                  <BookOpen size={16} />
                  <span
                    style={{
                      fontFamily: "var(--font-cormorant)",
                      letterSpacing: "0.1em",
                      fontWeight: activeTab === "summary" ? "500" : "300",
                    }}
                  >
                    Summary
                  </span>
                </div>

                <div
                  className={`tab-item ${
                    activeTab === "trends" ? "active" : ""
                  }`}
                  onClick={() => setActiveTab("trends")}
                  style={{
                    padding: "1rem 1.5rem",
                    cursor: "pointer",
                    color: activeTab === "trends" ? "#cdaf73" : "#a8a090",
                    borderBottom:
                      activeTab === "trends" ? "2px solid #cdaf73" : "none",
                    transition: "all 0.3s ease",
                    display: "flex",
                    alignItems: "center",
                    gap: "0.5rem",
                  }}
                  onMouseEnter={() => handleHover("tab-trends", true)}
                  onMouseLeave={() => handleHover("tab-trends", false)}
                >
                  <BarChart2 size={16} />
                  <span
                    style={{
                      fontFamily: "var(--font-cormorant)",
                      letterSpacing: "0.1em",
                      fontWeight: activeTab === "trends" ? "500" : "300",
                    }}
                  >
                    Genre Analysis
                  </span>
                </div>

                <div
                  className={`tab-item ${
                    activeTab === "timeline" ? "active" : ""
                  }`}
                  onClick={() => setActiveTab("timeline")}
                  style={{
                    padding: "1rem 1.5rem",
                    cursor: "pointer",
                    color: activeTab === "timeline" ? "#cdaf73" : "#a8a090",
                    borderBottom:
                      activeTab === "timeline" ? "2px solid #cdaf73" : "none",
                    transition: "all 0.3s ease",
                    display: "flex",
                    alignItems: "center",
                    gap: "0.5rem",
                  }}
                  onMouseEnter={() => handleHover("tab-timeline", true)}
                  onMouseLeave={() => handleHover("tab-timeline", false)}
                >
                  <Clock size={16} />
                  <span
                    style={{
                      fontFamily: "var(--font-cormorant)",
                      letterSpacing: "0.1em",
                      fontWeight: activeTab === "timeline" ? "500" : "300",
                    }}
                  >
                    Literary Timeline
                  </span>
                </div>
              </div>

              <div
                className="content-container fade-in"
                style={{
                  border: "1px solid rgba(205, 175, 115, 0.2)",
                  padding: "2rem",
                  background: "rgba(10, 31, 28, 0.5)",
                  minHeight: "400px",
                }}
              >
                {activeTab === "summary" && (
                  <div className="summary-content">
                    <div className="d-flex justify-content-between align-items-center mb-4">
                      <h3
                        className="font-heading mb-0"
                        style={{
                          color: "#cdaf73",
                          fontWeight: "400",
                          letterSpacing: "0.1em",
                        }}
                      >
                        {showQuestion ? showQuestion : "Book Summary"}
                      </h3>

                      <Button
                        variant="link"
                        className="text-decoration-none p-2 d-flex align-items-center"
                        onClick={handleResummarize}
                        disabled={isLoading}
                        style={{
                          color: "#cdaf73",
                          transition: "all 0.3s ease",
                        }}
                        onMouseEnter={() => handleHover("regenerate", true)}
                        onMouseLeave={() => handleHover("regenerate", false)}
                      >
                        <RefreshCw
                          size={16}
                          className="me-2"
                          style={{
                            transform: hoverStates["regenerate"]
                              ? "rotate(180deg)"
                              : "rotate(0deg)",
                            transition: "transform 0.6s ease",
                          }}
                        />
                        <span
                          className="d-none d-md-inline"
                          style={{
                            letterSpacing: "0.05em",
                            fontWeight: "300",
                            borderBottom: hoverStates["regenerate"]
                              ? "1px solid #cdaf73"
                              : "none",
                          }}
                        >
                          Regenerate
                        </span>
                      </Button>
                    </div>

                    <div className="decorative-line mb-4"></div>

                    {isLoading ? (
                      <div className="text-center py-5">
                        <div className="spinner-container">
                          <Spinner
                            animation="border"
                            style={{ color: "#cdaf73" }}
                          />
                        </div>
                        <p
                          style={{
                            color: "#a8a090",
                            marginTop: "1rem",
                            letterSpacing: "0.05em",
                          }}
                        >
                          Generating insights...
                        </p>
                      </div>
                    ) : (
                      <div key={animationKey} className="book-summary">
                        <TypeAnimation
                          sequence={[bookInfo, 3000]}
                          speed={{ type: "keyStrokeDelayInMs", value: 10 }}
                          omitDeletionAnimation={true}
                          style={{
                            whiteSpace: "pre-line",
                            fontSize: "1.1rem",
                            display: "block",
                            minHeight: "200px",
                            lineHeight: "1.8",
                            fontFamily: "var(--font-lora)",
                            color: "#e9e2d0",
                            letterSpacing: "0.02em",
                          }}
                          cursor={false}
                        />
                      </div>
                    )}
                  </div>
                )}

                {activeTab === "trends" && (
                  <div className="trends-content">
                    <h3
                      className="font-heading mb-4 text-center"
                      style={{
                        color: "#cdaf73",
                        fontWeight: "400",
                        letterSpacing: "0.1em",
                      }}
                    >
                      Genre Analysis
                    </h3>

                    <div className="decorative-line mb-4"></div>

                    <div className="d-flex justify-content-center align-items-center">
                      <div
                        className="position-relative"
                        style={{ width: "300px", height: "300px" }}
                      >
                        <canvas
                          ref={chartRef}
                          width={300}
                          height={300}
                          onClick={(e) => {
                            const rect =
                              chartRef.current.getBoundingClientRect();
                            const x = e.clientX - rect.left;
                            const y = e.clientY - rect.top;
                            const centerX = rect.width / 2;
                            const centerY = rect.height / 2;

                            // Calculate angle from center
                            const angle = Math.atan2(y - centerY, x - centerX);
                            let angleInDegrees = angle * (180 / Math.PI);
                            if (angleInDegrees < 0) angleInDegrees += 360;

                            // Calculate which segment was clicked
                            let startAngle = 0;
                            let selectedIndex = null;

                            for (let i = 0; i < sampleGenreData.length; i++) {
                              const sliceAngle =
                                (sampleGenreData[i].value / 100) * 360;
                              if (
                                angleInDegrees >= startAngle &&
                                angleInDegrees < startAngle + sliceAngle
                              ) {
                                selectedIndex = i;
                                break;
                              }
                              startAngle += sliceAngle;
                            }

                            setSelectedGenre(
                              selectedIndex === selectedGenre
                                ? null
                                : selectedIndex
                            );
                          }}
                          style={{ cursor: "pointer" }}
                        />

                        {selectedGenre !== null && (
                          <div
                            className="genre-details fade-in"
                            style={{
                              position: "absolute",
                              bottom: "-80px",
                              left: "0",
                              right: "0",
                              textAlign: "center",
                              padding: "1rem",
                              border: "1px solid rgba(205, 175, 115, 0.2)",
                              backgroundColor: "rgba(10, 31, 28, 0.8)",
                            }}
                          >
                            <h5
                              style={{
                                color: "#cdaf73",
                                fontFamily: "var(--font-cormorant)",
                                fontWeight: "400",
                                letterSpacing: "0.1em",
                                marginBottom: "0.5rem",
                              }}
                            >
                              {sampleGenreData[selectedGenre].name}
                            </h5>
                            <p
                              style={{
                                color: "#a8a090",
                                fontSize: "0.9rem",
                                marginBottom: "0",
                              }}
                            >
                              {sampleGenreData[selectedGenre].value}% of
                              literary works
                            </p>
                          </div>
                        )}
                      </div>
                    </div>

                    <div className="text-center mt-5">
                      <p style={{ color: "#a8a090", fontSize: "0.9rem" }}>
                        Click on a segment to see more details about each genre
                      </p>
                    </div>
                  </div>
                )}

                {activeTab === "timeline" && (
                  <div className="timeline-content">
                    <h3
                      className="font-heading mb-4 text-center"
                      style={{
                        color: "#cdaf73",
                        fontWeight: "400",
                        letterSpacing: "0.1em",
                      }}
                    >
                      Literary Timeline
                    </h3>

                    <div className="decorative-line mb-4"></div>

                    <div className="d-flex justify-content-center align-items-center">
                      <div style={{ width: "100%", height: "300px" }}>
                        <canvas ref={chartRef} width={600} height={300} />
                      </div>
                    </div>

                    <div className="text-center mt-4">
                      <p style={{ color: "#a8a090", fontSize: "0.9rem" }}>
                        Visualization of literary works published over time
                      </p>
                    </div>
                  </div>
                )}
              </div>

              <div
                className="questions-container mt-4 fade-in"
                style={{
                  border: "1px solid rgba(205, 175, 115, 0.2)",
                  padding: "2rem",
                  background: "rgba(10, 31, 28, 0.5)",
                }}
              >
                <h3
                  className="font-heading mb-4"
                  style={{
                    color: "#cdaf73",
                    fontWeight: "400",
                    letterSpacing: "0.1em",
                  }}
                >
                  Explore Deeper Insights
                </h3>

                <div className="decorative-line mb-4"></div>

                <Row className="g-4 mb-4">
                  {questions.q1 && (
                    <Col md={4}>
                      <div
                        className="question-circle"
                        onMouseEnter={() => handleHover("q1", true)}
                        onMouseLeave={() => handleHover("q1", false)}
                      >
                        <Button
                          onClick={() => qCalled("q1")}
                          disabled={isLoading}
                          style={{
                            backgroundColor: "transparent",
                            border: "1px solid rgba(205, 175, 115, 0.3)",
                            color: "#cdaf73",
                            borderRadius: "0",
                            padding: "1.5rem",
                            height: "100%",
                            width: "100%",
                            textAlign: "center",
                            fontFamily: "var(--font-cormorant)",
                            letterSpacing: "0.05em",
                            fontWeight: "300",
                            fontSize: "1rem",
                            position: "relative",
                            overflow: "hidden",
                          }}
                        >
                          {hoverStates["q1"] && (
                            <div
                              className="question-highlight"
                              style={{
                                position: "absolute",
                                top: 0,
                                left: 0,
                                width: "100%",
                                height: "100%",
                                background:
                                  "linear-gradient(135deg, rgba(205, 175, 115, 0.1) 0%, transparent 100%)",
                                opacity: 0,
                                animation: "fadeIn 0.3s forwards",
                              }}
                            ></div>
                          )}
                          {questions.q1}
                        </Button>
                      </div>
                    </Col>
                  )}

                  {questions.q2 && (
                    <Col md={4}>
                      <div
                        className="question-circle"
                        onMouseEnter={() => handleHover("q2", true)}
                        onMouseLeave={() => handleHover("q2", false)}
                      >
                        <Button
                          onClick={() => qCalled("q2")}
                          disabled={isLoading}
                          style={{
                            backgroundColor: "transparent",
                            border: "1px solid rgba(205, 175, 115, 0.3)",
                            color: "#cdaf73",
                            borderRadius: "0",
                            padding: "1.5rem",
                            height: "100%",
                            width: "100%",
                            textAlign: "center",
                            fontFamily: "var(--font-cormorant)",
                            letterSpacing: "0.05em",
                            fontWeight: "300",
                            fontSize: "1rem",
                            position: "relative",
                            overflow: "hidden",
                          }}
                        >
                          {hoverStates["q2"] && (
                            <div
                              className="question-highlight"
                              style={{
                                position: "absolute",
                                top: 0,
                                left: 0,
                                width: "100%",
                                height: "100%",
                                background:
                                  "linear-gradient(135deg, rgba(205, 175, 115, 0.1) 0%, transparent 100%)",
                                opacity: 0,
                                animation: "fadeIn 0.3s forwards",
                              }}
                            ></div>
                          )}
                          {questions.q2}
                        </Button>
                      </div>
                    </Col>
                  )}

                  {questions.q3 && (
                    <Col md={4}>
                      <div
                        className="question-circle"
                        onMouseEnter={() => handleHover("q3", true)}
                        onMouseLeave={() => handleHover("q3", false)}
                      >
                        <Button
                          onClick={() => qCalled("q3")}
                          disabled={isLoading}
                          style={{
                            backgroundColor: "transparent",
                            border: "1px solid rgba(205, 175, 115, 0.3)",
                            color: "#cdaf73",
                            borderRadius: "0",
                            padding: "1.5rem",
                            height: "100%",
                            width: "100%",
                            textAlign: "center",
                            fontFamily: "var(--font-cormorant)",
                            letterSpacing: "0.05em",
                            fontWeight: "300",
                            fontSize: "1rem",
                            position: "relative",
                            overflow: "hidden",
                          }}
                        >
                          {hoverStates["q3"] && (
                            <div
                              className="question-highlight"
                              style={{
                                position: "absolute",
                                top: 0,
                                left: 0,
                                width: "100%",
                                height: "100%",
                                background:
                                  "linear-gradient(135deg, rgba(205, 175, 115, 0.1) 0%, transparent 100%)",
                                opacity: 0,
                                animation: "fadeIn 0.3s forwards",
                              }}
                            ></div>
                          )}
                          {questions.q3}
                        </Button>
                      </div>
                    </Col>
                  )}
                </Row>

                <div
                  className="custom-question mt-4"
                  onMouseEnter={() => handleHover("custom-q", true)}
                  onMouseLeave={() => handleHover("custom-q", false)}
                >
                  <Form.Control
                    type="text"
                    placeholder="Ask your own question about this book..."
                    className="py-3 ps-4 pe-4 border-0"
                    style={{
                      backgroundColor: "rgba(0,0,0,0.2)",
                      color: "#e9e2d0",
                      borderRadius: "0",
                      fontSize: "1rem",
                      letterSpacing: "0.05em",
                      fontFamily: "var(--font-lora)",
                      borderLeft: hoverStates["custom-q"]
                        ? "3px solid #cdaf73"
                        : "3px solid transparent",
                      transition: "all 0.3s ease",
                    }}
                  />
                  <Button
                    className="mt-3 px-4 py-2"
                    style={{
                      backgroundColor: "transparent",
                      border: "1px solid rgba(205, 175, 115, 0.3)",
                      color: "#cdaf73",
                      borderRadius: "0",
                      letterSpacing: "0.1em",
                      fontWeight: "300",
                      transition: "all 0.3s ease",
                      position: "relative",
                      overflow: "hidden",
                    }}
                    onMouseEnter={() => handleHover("ask-btn", true)}
                    onMouseLeave={() => handleHover("ask-btn", false)}
                  >
                    {hoverStates["ask-btn"] && (
                      <div
                        className="btn-highlight"
                        style={{
                          position: "absolute",
                          top: 0,
                          left: 0,
                          width: "100%",
                          height: "100%",
                          background:
                            "linear-gradient(135deg, rgba(205, 175, 115, 0.1) 0%, transparent 100%)",
                          opacity: 0,
                          animation: "fadeIn 0.3s forwards",
                        }}
                      ></div>
                    )}
                    Ask Question
                  </Button>
                </div>
              </div>
            </Col>
          </Row>
        )}
      </Container>
    </div>
  );
}

export default Book;
