import { Container, Row, Col } from "react-bootstrap";

const About = () => {
  return (
    <div
      style={{
        backgroundColor: "#0a1f1c",
        color: "#e9e2d0",
        minHeight: "100vh",
      }}
    >
            {/* Hero Section */}     {" "}
      <div className="py-5 position-relative">
                {/* Assuming celestial-sun.png is in your public/ folder */}   
           {" "}
        <img
          src="/celestial-sun.png"
          alt="Sun Decoration"
          className="celestial-decoration top-right"
          style={{ opacity: 0.15 }}
        />
               {" "}
        <Container className="py-5">
                   {" "}
          <Row className="justify-content-center">
                       {" "}
            <Col lg={8} className="text-center">
                           {" "}
              <h1
                className="font-heading mb-4"
                style={{
                  color: "#cdaf73",
                  fontWeight: "300",
                  letterSpacing: "0.2em",
                  fontSize: "2.5rem",
                }}
              >
                                About Our Vision              {" "}
              </h1>
                            <div className="decorative-line mx-auto mb-4"></div>
                           {" "}
              <p
                className="mx-auto mb-5"
                style={{
                  maxWidth: "600px",
                  color: "#a8a090",
                  letterSpacing: "0.05em",
                  fontSize: "1.1rem",
                  lineHeight: "1.8",
                  fontWeight: "300",
                }}
              >
                                Discover the essence of literature through our
                AI-powered book summaries and insights              {" "}
              </p>
                         {" "}
            </Col>
                     {" "}
          </Row>
                 {" "}
        </Container>
             {" "}
      </div>
            {/* Main Content */}     {" "}
      <Container className="py-5">
               {" "}
        <Row className="mb-5 align-items-center">
                   {" "}
          <Col lg={5} className="mb-5 mb-lg-0">
                       {" "}
            <div
              className="arch-frame image-hover-effect" // Add a class for the hover effect
              style={{
                transition:
                  "transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out",
                cursor: "pointer",
                // Optional: Add initial shadow/border if you want a subtle effect even when not hovered
                // boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
              }}
            >
              {/* Corrected path for placeholder.svg. Dimensions are controlled by CSS. */}
                           {" "}
              <img
                src="/placeholder.svg" // Removed query parameters
                alt="About Literary Insights"
                className="img-fluid"
                style={
                  {
                    // You can set max-width/height or specific dimensions here
                    // if the arch-frame parent doesn't constrain it enough
                    // e.g., maxHeight: "600px", maxWidth: "400px", objectFit: "contain",
                  }
                }
              />
                         {" "}
            </div>
                     {" "}
          </Col>
                   {" "}
          <Col lg={7}>
                       {" "}
            <h2
              className="font-heading mb-4"
              style={{
                color: "#cdaf73",
                fontWeight: "300",
                letterSpacing: "0.15em",
                fontSize: "2rem",
              }}
            >
                            Our Literary Journey            {" "}
            </h2>
                        <div className="decorative-line mb-4"></div>           {" "}
            <div
              style={{
                fontFamily: "var(--font-lora)",
                color: "#e9e2d0",
                letterSpacing: "0.02em",
                lineHeight: "1.8",
              }}
            >
                           {" "}
              <p>
                                Welcome to our AI book summary app, designed to
                empower book lovers, book clubs, researchers, and              
                  literature enthusiasts. Our mission is to provide a valuable
                resource for discovering and exploring new                
                titles across a wide range of genres and topics.              {" "}
              </p>
                           {" "}
              <p style={{ color: "#cdaf73", fontWeight: "500" }}>
                                One of the standout features of our app is the
                Q&A section, where users can ask questions about the            
                    books and receive answers from our AI assistant. This
                feature is designed to provide additional                
                insights, clarification, and in-depth exploration of the
                literary works, further enhancing the user's                
                understanding and engagement.              {" "}
              </p>
                           {" "}
              <p>
                                Join us on this exciting journey as we redefine
                the way you discover and engage with books. Welcome to          
                      our book summary app – your gateway to a world of literary
                exploration, discovery, and                 knowledge-sharing.  
                           {" "}
              </p>
                         {" "}
            </div>
                     {" "}
          </Col>
                 {" "}
        </Row>
                <div className="decorative-line my-5"></div>       {" "}
        <Row className="justify-content-center text-center mb-5">
                   {" "}
          <Col lg={8}>
                       {" "}
            <h2
              className="font-heading mb-4"
              style={{
                color: "#cdaf73",
                fontWeight: "300",
                letterSpacing: "0.15em",
                fontSize: "2rem",
              }}
            >
                            Our Philosophy            {" "}
            </h2>
                       {" "}
            <p
              className="mx-auto"
              style={{
                maxWidth: "700px",
                color: "#a8a090",
                letterSpacing: "0.05em",
                fontSize: "1.1rem",
                lineHeight: "1.8",
                fontWeight: "300",
              }}
            >
                            We believe that literature is a gateway to
              understanding the human experience. Our AI-powered platform is    
                        designed to make the wisdom of books more accessible,
              allowing readers to explore new ideas and              
              perspectives with ease and depth.            {" "}
            </p>
                     {" "}
          </Col>
                 {" "}
        </Row>
               {" "}
        <Row className="g-4">
                   {" "}
          <Col md={4}>
                       {" "}
            <div
              className="p-4 h-100"
              style={{
                border: "1px solid rgba(205, 175, 115, 0.2)",
                background: "rgba(10, 31, 28, 0.5)",
              }}
            >
                           {" "}
              <h3
                className="font-heading mb-3"
                style={{
                  color: "#cdaf73",
                  fontWeight: "300",
                  letterSpacing: "0.1em",
                  fontSize: "1.5rem",
                }}
              >
                                Energy Healing              {" "}
              </h3>
                            <div className="decorative-line mb-3"></div>       
                   {" "}
              <p
                style={{
                  color: "#e9e2d0",
                  letterSpacing: "0.02em",
                  lineHeight: "1.8",
                }}
              >
                                Our summaries capture the essence and energy of
                each book, distilling complex ideas into accessible            
                    insights that resonate with readers.              {" "}
              </p>
                         {" "}
            </div>
                     {" "}
          </Col>
                   {" "}
          <Col md={4}>
                       {" "}
            <div
              className="p-4 h-100"
              style={{
                border: "1px solid rgba(205, 175, 115, 0.2)",
                background: "rgba(10, 31, 28, 0.5)",
              }}
            >
                           {" "}
              <h3
                className="font-heading mb-3"
                style={{
                  color: "#cdaf73",
                  fontWeight: "300",
                  letterSpacing: "0.1em",
                  fontSize: "1.5rem",
                }}
              >
                                Spiritual Activation              {" "}
              </h3>
                            <div className="decorative-line mb-3"></div>       
                   {" "}
              <p
                style={{
                  color: "#e9e2d0",
                  letterSpacing: "0.02em",
                  lineHeight: "1.8",
                }}
              >
                                Books have the power to awaken new perspectives
                and transform thinking. Our platform helps activate            
                    these insights through thoughtful analysis.              {" "}
              </p>
                         {" "}
            </div>
                     {" "}
          </Col>
                   {" "}
          <Col md={4}>
                       {" "}
            <div
              className="p-4 h-100"
              style={{
                border: "1px solid rgba(205, 175, 115, 0.2)",
                background: "rgba(10, 31, 28, 0.5)",
              }}
            >
                           {" "}
              <h3
                className="font-heading mb-3"
                style={{
                  color: "#cdaf73",
                  fontWeight: "300",
                  letterSpacing: "0.1em",
                  fontSize: "1.5rem",
                }}
              >
                                Intuitive Guidance              {" "}
              </h3>
                            <div className="decorative-line mb-3"></div>       
                   {" "}
              <p
                style={{
                  color: "#e9e2d0",
                  letterSpacing: "0.02em",
                  lineHeight: "1.8",
                }}
              >
                                Our AI assistant provides intuitive guidance
                through books, helping you discover connections and            
                    meanings that might otherwise remain hidden.              {" "}
              </p>
                         {" "}
            </div>
                     {" "}
          </Col>
                 {" "}
        </Row>
             {" "}
      </Container>
           {" "}
      <div className="py-5 position-relative">
                {/* Assuming celestial-moon.png is in your public/ folder */}   
           {" "}
        <img
          src="/celestial-moon.png"
          alt="Moon Decoration"
          className="celestial-decoration bottom-left"
          style={{ opacity: 0.15 }}
        />
               {" "}
        <Container className="py-5">
                   {" "}
          <Row className="justify-content-center">
                       {" "}
            <Col lg={8} className="text-center">
                           {" "}
              <h2
                className="font-heading mb-4"
                style={{
                  color: "#cdaf73",
                  fontWeight: "300",
                  letterSpacing: "0.15em",
                  fontSize: "2rem",
                }}
              >
                                Begin Your Literary Journey              {" "}
              </h2>
                            <div className="decorative-line mx-auto mb-4"></div>
                           {" "}
              <p
                className="mx-auto mb-5"
                style={{
                  maxWidth: "600px",
                  color: "#a8a090",
                  letterSpacing: "0.05em",
                  fontSize: "1.1rem",
                  lineHeight: "1.8",
                  fontWeight: "300",
                }}
              >
                                Explore the depths of literature with our
                AI-powered insights and discover new dimensions in your        
                        favorite books              {" "}
              </p>
                           {" "}
              <a
                href="/"
                className="btn px-4 py-2"
                style={{
                  backgroundColor: "transparent",
                  border: "1px solid rgba(205, 175, 115, 0.3)",
                  color: "#cdaf73",
                  borderRadius: "0",
                  letterSpacing: "0.1em",
                  fontWeight: "300",
                }}
              >
                                Explore Books              {" "}
              </a>
                         {" "}
            </Col>
                     {" "}
          </Row>
                 {" "}
        </Container>
             {" "}
      </div>
         {" "}
    </div>
  );
};

export default About;
