"use client";

import { useState } from "react";
import axios from "axios";
import { TypeAnimation } from "react-type-animation";
import {
  RefreshCw,
  Search,
  MessageSquare,
  Sparkles,
  BookText,
  ChevronRight,
} from "lucide-react";
import {
  Button,
  Form,
  Card,
  Container,
  Row,
  Col,
  Spinner,
} from "react-bootstrap";

function Book() {
  const [bookName, setBookName] = useState("");
  const [bookInfo, setBookInfo] = useState("");
  const [bookUrl, setBookUrl] = useState("");
  const [animationKey, setAnimationKey] = useState(0);
  const [questions, setQuestions] = useState({});
  const [showQuestion, setShowQuestion] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [bookMeta, setBookMeta] = useState({ title: "", author: "" });
  const [activeTab, setActiveTab] = useState("summary");

  const qCalled = async (qKey) => {
    try {
      setIsLoading(true);
      setError("");

      const questionRes = await axios.get(`http://127.0.0.1:5000/question/`);
      const questionText = questionRes.data.q;

      const ansRes = await axios.post(`http://127.0.0.1:5000/questions/`, {
        q: questionText,
      });

      setQuestions((prev) => ({ ...prev, [qKey]: questionText }));
      setBookInfo(ansRes.data.data);
      setShowQuestion(questionText);
      setAnimationKey((prev) => prev + 1);
      setActiveTab("insights");
    } catch (error) {
      console.error("Error fetching question or answer:", error);
      setError("Failed to fetch question or answer. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!bookName.trim()) return;

    try {
      setIsLoading(true);
      setError("");
      setBookInfo("");
      setBookUrl("");

      const response = await axios.get(
        `http://127.0.0.1:5000/info/${bookName}`
      );
      const bookmeta = await axios.get(`http://127.0.0.1:5000/bookinfo`);
      setBookMeta(bookmeta.data);

      const cover_img = await axios.get(
        `https://bookcover.longitood.com/bookcover`,
        {
          params: {
            book_title: bookmeta.data.title,
            author_name: bookmeta.data.author,
          },
        }
      );

      const questionData = await axios.get(`http://127.0.0.1:5000/questions/`);

      // Summarize the full text
      const summaryRes = await axios.post(`http://127.0.0.1:5000/summarize`, {
        text: response.data.data,
      });

      setQuestions(questionData.data);
      setBookUrl(cover_img.data.url);
      setBookInfo(summaryRes.data.summary);
      setShowQuestion("");
      setAnimationKey((prev) => prev + 1);
      setActiveTab("summary");
    } catch (error) {
      console.error("Error fetching book data:", error);
      setError(
        "Failed to fetch book information. Please check the book name and try again."
      );
    } finally {
      setIsLoading(false);
    }
  };

  const handleResummarize = async () => {
    try {
      setIsLoading(true);
      setError("");

      const res = await axios.post(`http://127.0.0.1:5000/summarize`, {
        text: bookInfo,
      });
      setBookInfo(res.data.summary);
      setShowQuestion("Re-summarized:");
      setAnimationKey((prev) => prev + 1);
    } catch (err) {
      console.error("Error re-summarizing:", err);
      setError("Failed to re-summarize. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div
      className="book-page py-5"
      style={{
        backgroundColor: "#0f172a",
        minHeight: "100vh",
        color: "#f8fafc",
      }}
    >
      <Container>
        <Row className="justify-content-center mb-5">
          <Col md={8} lg={6}>
            <Card
              className="border-0 shadow-lg"
              style={{
                background: "linear-gradient(145deg, #1e293b, #111827)",
                borderRadius: "16px",
                overflow: "hidden",
              }}
            >
              <Card.Body className="p-4">
                <h2
                  className="mb-4 font-serif fw-bold text-center"
                  style={{ color: "#f8fafc" }}
                >
                  Discover Book Insights
                </h2>
                <Form onSubmit={handleSubmit}>
                  <div className="position-relative">
                    <Form.Control
                      type="text"
                      value={bookName}
                      onChange={(event) => setBookName(event.target.value)}
                      placeholder="Enter a book title..."
                      className="py-3 ps-5 pe-4 border-0 shadow-sm"
                      style={{
                        backgroundColor: "rgba(255,255,255,0.05)",
                        color: "#f8fafc",
                        borderRadius: "12px",
                        fontSize: "1rem",
                      }}
                    />
                    <Search
                      size={18}
                      className="position-absolute"
                      style={{
                        left: "15px",
                        top: "50%",
                        transform: "translateY(-50%)",
                        color: "#94a3b8",
                      }}
                    />
                    <Button
                      type="submit"
                      className="position-absolute d-flex align-items-center justify-content-center"
                      style={{
                        right: "8px",
                        top: "50%",
                        transform: "translateY(-50%)",
                        width: "36px",
                        height: "36px",
                        borderRadius: "8px",
                        background:
                          "linear-gradient(135deg, #9333ea 0%, #4f46e5 100%)",
                        border: "none",
                      }}
                      disabled={isLoading || !bookName.trim()}
                    >
                      {isLoading ? (
                        <Spinner size="sm" animation="border" />
                      ) : (
                        <ChevronRight size={18} />
                      )}
                    </Button>
                  </div>
                </Form>
              </Card.Body>
            </Card>
          </Col>
        </Row>

        {error && (
          <Row className="justify-content-center mb-4">
            <Col md={10}>
              <div
                className="p-3 rounded-3"
                style={{
                  backgroundColor: "rgba(239, 68, 68, 0.1)",
                  color: "#f87171",
                  border: "1px solid rgba(239, 68, 68, 0.2)",
                }}
              >
                {error}
              </div>
            </Col>
          </Row>
        )}

        {bookUrl && bookInfo && (
          <Row>
            <Col md={4} lg={3} className="mb-4">
              <Card
                className="border-0 shadow-lg h-100"
                style={{
                  background: "linear-gradient(145deg, #1e293b, #111827)",
                  borderRadius: "16px",
                  overflow: "hidden",
                }}
              >
                <div className="p-4 text-center">
                  <div
                    className="book-cover-container mb-4"
                    style={{
                      position: "relative",
                      borderRadius: "8px",
                      overflow: "hidden",
                      boxShadow: "0 10px 30px -10px rgba(0,0,0,0.5)",
                      maxWidth: "200px",
                      margin: "0 auto",
                    }}
                  >
                    <img
                      src={bookUrl || "/placeholder.svg"}
                      alt={bookMeta.title}
                      className="img-fluid"
                      style={{
                        width: "100%",
                        height: "auto",
                        objectFit: "cover",
                      }}
                    />
                    <div
                      style={{
                        position: "absolute",
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        background:
                          "linear-gradient(to bottom, rgba(0,0,0,0) 70%, rgba(0,0,0,0.8) 100%)",
                      }}
                    ></div>
                  </div>

                  <h4
                    className="font-serif fw-bold mb-2"
                    style={{ color: "#f8fafc" }}
                  >
                    {bookMeta.title}
                  </h4>
                  {bookMeta.author && (
                    <p className="mb-4" style={{ color: "#94a3b8" }}>
                      by {bookMeta.author}
                    </p>
                  )}

                  <div className="d-flex justify-content-center">
                    <Button
                      variant="outline-light"
                      size="sm"
                      className="me-2 rounded-pill px-3 py-1"
                      style={{
                        fontSize: "0.8rem",
                        borderColor: "rgba(255,255,255,0.1)",
                      }}
                    >
                      Add to Library
                    </Button>
                    <Button
                      variant="outline-light"
                      size="sm"
                      className="rounded-pill px-3 py-1"
                      style={{
                        fontSize: "0.8rem",
                        borderColor: "rgba(255,255,255,0.1)",
                      }}
                    >
                      Share
                    </Button>
                  </div>
                </div>
              </Card>
            </Col>

            <Col md={8} lg={9}>
              <Card
                className="border-0 shadow-lg mb-4"
                style={{
                  background: "linear-gradient(145deg, #1e293b, #111827)",
                  borderRadius: "16px",
                  overflow: "hidden",
                }}
              >
                <Card.Header
                  className="d-flex border-0"
                  style={{ backgroundColor: "rgba(0,0,0,0.2)" }}
                >
                  <div
                    className={`tab-button py-3 px-4 ${
                      activeTab === "summary" ? "active" : ""
                    }`}
                    onClick={() => setActiveTab("summary")}
                    style={{
                      cursor: "pointer",
                      position: "relative",
                      color: activeTab === "summary" ? "#f8fafc" : "#94a3b8",
                      fontWeight: activeTab === "summary" ? "600" : "normal",
                    }}
                  >
                    <div className="d-flex align-items-center">
                      <BookText size={18} className="me-2" />
                      Summary
                    </div>
                    {activeTab === "summary" && (
                      <div
                        style={{
                          position: "absolute",
                          bottom: 0,
                          left: "10%",
                          width: "80%",
                          height: "3px",
                          background:
                            "linear-gradient(90deg, #9333ea, #4f46e5)",
                          borderRadius: "3px 3px 0 0",
                        }}
                      ></div>
                    )}
                  </div>

                  <div
                    className={`tab-button py-3 px-4 ${
                      activeTab === "insights" ? "active" : ""
                    }`}
                    onClick={() => setActiveTab("insights")}
                    style={{
                      cursor: "pointer",
                      position: "relative",
                      color: activeTab === "insights" ? "#f8fafc" : "#94a3b8",
                      fontWeight: activeTab === "insights" ? "600" : "normal",
                    }}
                  >
                    <div className="d-flex align-items-center">
                      <Sparkles size={18} className="me-2" />
                      Insights
                    </div>
                    {activeTab === "insights" && (
                      <div
                        style={{
                          position: "absolute",
                          bottom: 0,
                          left: "10%",
                          width: "80%",
                          height: "3px",
                          background:
                            "linear-gradient(90deg, #9333ea, #4f46e5)",
                          borderRadius: "3px 3px 0 0",
                        }}
                      ></div>
                    )}
                  </div>

                  <div className="ms-auto">
                    <Button
                      variant="link"
                      className="text-decoration-none p-2 d-flex align-items-center"
                      onClick={handleResummarize}
                      disabled={isLoading}
                      style={{ color: "#94a3b8" }}
                    >
                      <RefreshCw size={16} className="me-2" />
                      <span className="d-none d-md-inline">Regenerate</span>
                    </Button>
                  </div>
                </Card.Header>

                <Card.Body className="p-4">
                  {isLoading ? (
                    <div className="text-center py-5">
                      <div
                        className="mb-3"
                        style={{
                          width: "48px",
                          height: "48px",
                          margin: "0 auto",
                          background:
                            "linear-gradient(135deg, #9333ea 0%, #4f46e5 100%)",
                          borderRadius: "50%",
                          display: "flex",
                          alignItems: "center",
                          justifyContent: "center",
                        }}
                      >
                        <Spinner
                          animation="border"
                          variant="light"
                          style={{ width: "24px", height: "24px" }}
                        />
                      </div>
                      <p style={{ color: "#94a3b8" }}>Generating insights...</p>
                    </div>
                  ) : (
                    <>
                      {showQuestion && (
                        <div
                          className="mb-4 p-3 rounded-3"
                          style={{
                            backgroundColor: "rgba(147, 51, 234, 0.1)",
                            borderLeft: "3px solid #9333ea",
                          }}
                        >
                          <h5
                            className="font-serif mb-0"
                            style={{ color: "#d8b4fe" }}
                          >
                            {showQuestion}
                          </h5>
                        </div>
                      )}

                      <div key={animationKey} className="book-summary">
                        <TypeAnimation
                          sequence={[bookInfo, 3000]}
                          speed={{ type: "keyStrokeDelayInMs", value: 10 }}
                          omitDeletionAnimation={true}
                          style={{
                            whiteSpace: "pre-line",
                            fontSize: "1.1rem",
                            display: "block",
                            minHeight: "200px",
                            lineHeight: "1.8",
                            fontFamily: "Georgia, serif",
                            color: "#e2e8f0",
                          }}
                          cursor={false}
                        />
                      </div>
                    </>
                  )}
                </Card.Body>
              </Card>

              <Card
                className="border-0 shadow-lg"
                style={{
                  background: "linear-gradient(145deg, #1e293b, #111827)",
                  borderRadius: "16px",
                  overflow: "hidden",
                }}
              >
                <Card.Header
                  className="border-0"
                  style={{ backgroundColor: "rgba(0,0,0,0.2)" }}
                >
                  <div className="d-flex align-items-center">
                    <MessageSquare
                      size={18}
                      className="me-2"
                      style={{ color: "#94a3b8" }}
                    />
                    <h5
                      className="mb-0 font-serif"
                      style={{ color: "#f8fafc" }}
                    >
                      Ask Questions
                    </h5>
                  </div>
                </Card.Header>

                <Card.Body className="p-4">
                  <Row className="g-3">
                    {questions.q1 && (
                      <Col md={4}>
                        <Button
                          className="w-100 h-100 py-3 px-4 text-start border-0 position-relative overflow-hidden"
                          onClick={() => qCalled("q1")}
                          disabled={isLoading}
                          style={{
                            backgroundColor: "rgba(255,255,255,0.05)",
                            color: "#e2e8f0",
                            borderRadius: "12px",
                            transition: "all 0.3s ease",
                          }}
                        >
                          <div
                            style={{
                              position: "absolute",
                              top: 0,
                              left: 0,
                              width: "4px",
                              height: "100%",
                              background:
                                "linear-gradient(to bottom, #9333ea, #4f46e5)",
                            }}
                          ></div>
                          {questions.q1}
                        </Button>
                      </Col>
                    )}

                    {questions.q2 && (
                      <Col md={4}>
                        <Button
                          className="w-100 h-100 py-3 px-4 text-start border-0 position-relative overflow-hidden"
                          onClick={() => qCalled("q2")}
                          disabled={isLoading}
                          style={{
                            backgroundColor: "rgba(255,255,255,0.05)",
                            color: "#e2e8f0",
                            borderRadius: "12px",
                            transition: "all 0.3s ease",
                          }}
                        >
                          <div
                            style={{
                              position: "absolute",
                              top: 0,
                              left: 0,
                              width: "4px",
                              height: "100%",
                              background:
                                "linear-gradient(to bottom, #9333ea, #4f46e5)",
                            }}
                          ></div>
                          {questions.q2}
                        </Button>
                      </Col>
                    )}

                    {questions.q3 && (
                      <Col md={4}>
                        <Button
                          className="w-100 h-100 py-3 px-4 text-start border-0 position-relative overflow-hidden"
                          onClick={() => qCalled("q3")}
                          disabled={isLoading}
                          style={{
                            backgroundColor: "rgba(255,255,255,0.05)",
                            color: "#e2e8f0",
                            borderRadius: "12px",
                            transition: "all 0.3s ease",
                          }}
                        >
                          <div
                            style={{
                              position: "absolute",
                              top: 0,
                              left: 0,
                              width: "4px",
                              height: "100%",
                              background:
                                "linear-gradient(to bottom, #9333ea, #4f46e5)",
                            }}
                          ></div>
                          {questions.q3}
                        </Button>
                      </Col>
                    )}
                  </Row>

                  <div className="mt-4 position-relative">
                    <Form.Control
                      type="text"
                      placeholder="Ask your own question about this book..."
                      className="py-3 ps-5 pe-4 border-0 shadow-sm"
                      style={{
                        backgroundColor: "rgba(255,255,255,0.05)",
                        color: "#f8fafc",
                        borderRadius: "12px",
                        fontSize: "1rem",
                      }}
                    />
                    <MessageSquare
                      size={18}
                      className="position-absolute"
                      style={{
                        left: "15px",
                        top: "50%",
                        transform: "translateY(-50%)",
                        color: "#94a3b8",
                      }}
                    />
                    <Button
                      className="position-absolute d-flex align-items-center justify-content-center"
                      style={{
                        right: "8px",
                        top: "50%",
                        transform: "translateY(-50%)",
                        width: "36px",
                        height: "36px",
                        borderRadius: "8px",
                        background:
                          "linear-gradient(135deg, #9333ea 0%, #4f46e5 100%)",
                        border: "none",
                      }}
                    >
                      <ChevronRight size={18} />
                    </Button>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        )}
      </Container>
    </div>
  );
}

export default Book;
