import { Book, Search, User, Menu } from "lucide-react";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

function Navbars() {
  return (
    <Navbar
      expand="lg"
      className="navbar-dark py-3"
      style={{
        backgroundColor: "#0f172a",
        borderBottom: "1px solid rgba(255,255,255,0.05)",
      }}
    >
      <Container>
        <Link
          to="/"
          className="navbar-brand d-flex align-items-center"
          style={{ textDecoration: "none" }}
        >
          <div
            className="me-3 p-2 rounded-circle"
            style={{
              background: "linear-gradient(135deg, #9333ea 0%, #4f46e5 100%)",
            }}
          >
            <Book className="text-white" size={20} />
          </div>
          <h4
            className="mb-0 font-serif fw-bold"
            style={{ letterSpacing: "-0.02em", color: "#f8fafc" }}
          >
            Insightful<span style={{ color: "#9333ea" }}>.</span>
          </h4>
        </Link>

        <Navbar.Toggle aria-controls="basic-navbar-nav">
          <Menu size={24} />
        </Navbar.Toggle>

        <Navbar.Collapse id="basic-navbar-nav" className="justify-content-end">
          <Nav className="align-items-center">
            <div className="position-relative me-3 d-none d-lg-block">
              <input
                type="text"
                placeholder="Search books..."
                className="py-2 ps-4 pe-3 rounded-pill border-0"
                style={{
                  backgroundColor: "rgba(255,255,255,0.05)",
                  color: "#f8fafc",
                  width: "200px",
                  fontSize: "0.9rem",
                }}
              />
              <Search
                size={16}
                className="position-absolute"
                style={{
                  left: "10px",
                  top: "50%",
                  transform: "translateY(-50%)",
                  color: "#94a3b8",
                }}
              />
            </div>

            <Link to="/" className="nav-link mx-2 text-light-emphasis">
              Home
            </Link>
            <Link to="/about" className="nav-link mx-2 text-light-emphasis">
              About
            </Link>
            <Link to="/summaries" className="nav-link mx-2 text-light-emphasis">
              Summaries
            </Link>
            <Link
              to="/account"
              className="ms-3 p-2 rounded-circle d-flex align-items-center justify-content-center"
              style={{
                background: "rgba(255,255,255,0.05)",
                transition: "all 0.2s ease",
              }}
            >
              <User size={18} className="text-light-emphasis" />
            </Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Navbars;
