"use client";

import { useState, useEffect } from "react";
import { ChevronLeft, ChevronRight } from "lucide-react";
import { Button } from "react-bootstrap";

const featuredBooks = [
  {
    id: 1,
    title: "The Great Gatsby",
    author: "F. Scott Fitzgerald",
    coverUrl: "https://placehold.co/400x600?text=The+Great+Gatsby",
    year: "1925",
    genre: "Classic Fiction",
    description:
      "A tale of wealth, love, and the American Dream set in the Roaring Twenties, following the mysterious millionaire Jay Gatsby.",
  },
  {
    id: 2,
    title: "To Kill a Mockingbird",
    author: "Harper Lee",
    coverUrl: "https://placehold.co/400x600?text=To+Kill+a+Mockingbird",
    year: "1960",
    genre: "Southern Gothic",
    description:
      "A story of racial injustice and moral growth in the American South during the Great Depression, seen through the eyes of a young girl.",
  },
  {
    id: 3,
    title: "1984",
    author: "George Orwell",
    coverUrl: "https://placehold.co/400x600?text=1984",
    year: "1949",
    genre: "Dystopian Fiction",
    description:
      "A chilling portrayal of a totalitarian future where critical thought is suppressed under a surveillance state.",
  },
  {
    id: 4,
    title: "Pride and Prejudice",
    author: "Jane Austen",
    coverUrl: "https://placehold.co/400x600?text=Pride+and+Prejudice",
    year: "1813",
    genre: "Romance",
    description:
      "A romantic novel of manners that follows the character development of Elizabeth Bennet in Georgian England.",
  },
  {
    id: 5,
    title: "The Odyssey",
    author: "Homer",
    coverUrl: "https://placehold.co/400x600?text=The+Odyssey",
    year: "8th Century BCE",
    genre: "Epic Poetry",
    description:
      "An ancient Greek epic poem that follows the hero Odysseus on his journey home after the Trojan War.",
  },
];

const FeaturedBooksCarousel = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(true);
  const [visibleItems, setVisibleItems] = useState(3);

  useEffect(() => {
    const updateVisibleItems = () => {
      const width = window.innerWidth;
      if (width < 576) setVisibleItems(1);
      else if (width < 768) setVisibleItems(2);
      else setVisibleItems(3);
    };

    updateVisibleItems();
    window.addEventListener("resize", updateVisibleItems);
    return () => window.removeEventListener("resize", updateVisibleItems);
  }, []);

  useEffect(() => {
    if (!isPlaying) return;
    const interval = setInterval(() => {
      setActiveIndex((prevIndex) => (prevIndex + 1) % featuredBooks.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [isPlaying]);

  const prevSlide = () => {
    setActiveIndex((prevIndex) =>
      prevIndex === 0 ? featuredBooks.length - 1 : prevIndex - 1
    );
  };

  const nextSlide = () => {
    setActiveIndex((prevIndex) => (prevIndex + 1) % featuredBooks.length);
  };

  const goToSlide = (index) => {
    setActiveIndex(index);
    setIsPlaying(false);
  };

  const getVisibleBooks = () => {
    const books = [];
    for (let i = 0; i < visibleItems; i++) {
      books.push(featuredBooks[(activeIndex + i) % featuredBooks.length]);
    }
    return books;
  };

  return (
    <div
      className="featured-books-carousel position-relative"
      style={{
        maxWidth: "1300px",
        margin: "auto",
        padding: "2rem 0",
        minHeight: "650px",
      }}
    >
      <div className="carousel-inner d-flex justify-content-center align-items-center">
        {getVisibleBooks().map((book) => (
          <div
            key={book.id}
            className="carousel-item"
            style={{
              flex: `0 0 ${100 / visibleItems}%`,
              padding: "0 15px",
              height: "100%",
            }}
          >
            <div
              className="book-card position-relative"
              style={{ height: "100%" }}
            >
              <div
                className="book-cover-wrapper"
                style={{ height: "100%", overflow: "hidden" }}
              >
                <img
                  src={book.coverUrl}
                  alt={book.title}
                  style={{
                    width: "100%",
                    height: "100%",
                    objectFit: "cover",
                    borderRadius: "6px",
                    boxShadow: "0 10px 30px rgba(0,0,0,0.3)",
                  }}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src =
                      "https://placehold.co/400x600?text=Image+Not+Found";
                  }}
                />
                <div
                  className="book-info-overlay"
                  style={{
                    position: "absolute",
                    bottom: 0,
                    left: 0,
                    right: 0,
                    padding: "1rem",
                    color: "#fff",
                    background:
                      "linear-gradient(to top, rgba(0,0,0,0.8), transparent)",
                  }}
                >
                  <h5 className="mb-1" style={{ fontWeight: "bold" }}>
                    {book.title}
                  </h5>
                  <small style={{ opacity: 0.8 }}>
                    {book.author} &middot; {book.year}
                  </small>
                  <p
                    style={{
                      fontSize: "0.8rem",
                      marginTop: "0.5rem",
                      opacity: 0.7,
                    }}
                  >
                    {book.genre}
                  </p>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Navigation Buttons */}
      <Button
        variant="dark"
        className="carousel-control-prev"
        style={{
          position: "absolute",
          top: "50%",
          left: "10px",
          transform: "translateY(-50%)",
          zIndex: 20,
          backgroundColor: "rgba(0,0,0,0.4)",
          border: "none",
        }}
        onClick={() => {
          prevSlide();
          setIsPlaying(false);
        }}
      >
        <ChevronLeft size={24} />
      </Button>

      <Button
        variant="dark"
        className="carousel-control-next"
        style={{
          position: "absolute",
          top: "50%",
          right: "10px",
          transform: "translateY(-50%)",
          zIndex: 20,
          backgroundColor: "rgba(0,0,0,0.4)",
          border: "none",
        }}
        onClick={() => {
          nextSlide();
          setIsPlaying(false);
        }}
      >
        <ChevronRight size={24} />
      </Button>

      {/* Carousel Dots */}
      <div className="carousel-dots text-center mt-3">
        {featuredBooks.map((_, index) => (
          <span
            key={index}
            onClick={() => goToSlide(index)}
            style={{
              display: "inline-block",
              width: 12,
              height: 12,
              borderRadius: "50%",
              background: index === activeIndex ? "#cdaf73" : "#ccc",
              margin: "0 6px",
              cursor: "pointer",
              transition: "all 0.3s",
            }}
          ></span>
        ))}
      </div>
    </div>
  );
};

export default FeaturedBooksCarousel;
