"use client";

import { useState } from "react";
import axios from "axios";
import { TypeAnimation } from "react-type-animation";
import {
  BookIcon,
  RefreshCw,
  Search,
  BookOpen,
  HelpCircle,
} from "lucide-react";
import {
  Button,
  Form,
  Card,
  Container,
  Row,
  Col,
  Spinner,
  Alert,
} from "react-bootstrap";

function Book() {
  const [bookName, setBookName] = useState("");
  const [bookInfo, setBookInfo] = useState("");
  const [bookUrl, setBookUrl] = useState("");
  const [animationKey, setAnimationKey] = useState(0);
  const [questions, setQuestions] = useState({});
  const [showQuestion, setShowQuestion] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [bookMeta, setBookMeta] = useState({ title: "", author: "" });

  const qCalled = async (qKey) => {
    try {
      setIsLoading(true);
      setError("");

      const questionRes = await axios.get(`http://127.0.0.1:5000/question/`);
      const questionText = questionRes.data.q;

      const ansRes = await axios.post(`http://127.0.0.1:5000/questions/`, {
        q: questionText,
      });

      setQuestions((prev) => ({ ...prev, [qKey]: questionText }));
      setBookInfo(ansRes.data.data);
      setShowQuestion(questionText);
      setAnimationKey((prev) => prev + 1);
    } catch (error) {
      console.error("Error fetching question or answer:", error);
      setError("Failed to fetch question or answer. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!bookName.trim()) return;

    try {
      setIsLoading(true);
      setError("");
      setBookInfo("");
      setBookUrl("");

      const response = await axios.get(
        `http://127.0.0.1:5000/info/${bookName}`
      );
      const bookmeta = await axios.get(`http://127.0.0.1:5000/bookinfo`);
      setBookMeta(bookmeta.data);

      const cover_img = await axios.get(
        `https://bookcover.longitood.com/bookcover`,
        {
          params: {
            book_title: bookmeta.data.title,
            author_name: bookmeta.data.author,
          },
        }
      );

      const questionData = await axios.get(`http://127.0.0.1:5000/questions/`);

      // Summarize the full text
      const summaryRes = await axios.post(`http://127.0.0.1:5000/summarize`, {
        text: response.data.data,
      });

      setQuestions(questionData.data);
      setBookUrl(cover_img.data.url);
      setBookInfo(summaryRes.data.summary);
      setShowQuestion("");
      setAnimationKey((prev) => prev + 1);
    } catch (error) {
      console.error("Error fetching book data:", error);
      setError(
        "Failed to fetch book information. Please check the book name and try again."
      );
    } finally {
      setIsLoading(false);
    }
  };

  const handleResummarize = async () => {
    try {
      setIsLoading(true);
      setError("");

      const res = await axios.post(`http://127.0.0.1:5000/summarize`, {
        text: bookInfo,
      });
      setBookInfo(res.data.summary);
      setShowQuestion("Re-summarized:");
      setAnimationKey((prev) => prev + 1);
    } catch (err) {
      console.error("Error re-summarizing:", err);
      setError("Failed to re-summarize. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Container className="py-5">
      <Row className="justify-content-center mb-5">
        <Col md={8} lg={6}>
          <Card className="shadow-sm">
            <Card.Body>
              <div className="d-flex align-items-center mb-4">
                <BookIcon size={28} className="text-primary me-2" />
                <h2 className="mb-0">Book Summary</h2>
              </div>

              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                  <Form.Label>Enter a book name to get its summary</Form.Label>
                  <div className="d-flex">
                    <Form.Control
                      type="text"
                      value={bookName}
                      onChange={(event) => setBookName(event.target.value)}
                      placeholder="e.g., To Kill a Mockingbird"
                      required
                    />
                    <Button
                      type="submit"
                      variant="primary"
                      className="ms-2"
                      disabled={isLoading || !bookName.trim()}
                    >
                      {isLoading ? (
                        <Spinner size="sm" animation="border" />
                      ) : (
                        <Search size={18} />
                      )}
                    </Button>
                  </div>
                </Form.Group>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {error && (
        <Row className="justify-content-center mb-4">
          <Col md={10}>
            <Alert variant="danger">{error}</Alert>
          </Col>
        </Row>
      )}

      {bookUrl && bookInfo && (
        <Row>
          <Col md={4} lg={3} className="mb-4">
            <Card className="shadow-sm h-100">
              <Card.Img
                variant="top"
                src={bookUrl}
                alt={bookMeta.title}
                className="img-fluid"
                style={{
                  maxHeight: "400px",
                  objectFit: "contain",
                  padding: "1rem",
                }}
              />
              <Card.Body>
                {bookMeta.title && (
                  <Card.Title className="fs-5">{bookMeta.title}</Card.Title>
                )}
                {bookMeta.author && (
                  <Card.Subtitle className="mb-2 text-muted">
                    by {bookMeta.author}
                  </Card.Subtitle>
                )}
              </Card.Body>
            </Card>
          </Col>

          <Col md={8} lg={9}>
            <Card className="shadow-sm mb-4">
              <Card.Header className="bg-light d-flex justify-content-between align-items-center">
                <div className="d-flex align-items-center">
                  <BookOpen size={18} className="me-2" />
                  <h5 className="mb-0">
                    {showQuestion ? showQuestion : "Book Summary"}
                  </h5>
                </div>
                <Button
                  variant="outline-success"
                  size="sm"
                  onClick={handleResummarize}
                  disabled={isLoading}
                >
                  <RefreshCw size={16} className="me-1" />
                  Summarize Again
                </Button>
              </Card.Header>
              <Card.Body className="summary-container">
                {isLoading ? (
                  <div className="text-center py-5">
                    <Spinner animation="border" variant="primary" />
                    <p className="mt-3">Processing your request...</p>
                  </div>
                ) : (
                  <div key={animationKey} className="book-summary">
                    <TypeAnimation
                      sequence={[bookInfo, 3000]}
                      speed={{ type: "keyStrokeDelayInMs", value: 10 }}
                      omitDeletionAnimation={true}
                      style={{
                        whiteSpace: "pre-line",
                        fontSize: "1.1rem",
                        display: "block",
                        minHeight: "200px",
                        lineHeight: "1.6",
                        fontFamily: "Georgia, serif",
                      }}
                      cursor={false}
                    />
                  </div>
                )}
              </Card.Body>
            </Card>

            <Card className="shadow-sm">
              <Card.Header className="bg-light">
                <div className="d-flex align-items-center">
                  <HelpCircle size={18} className="me-2" />
                  <h5 className="mb-0">Ask Questions</h5>
                </div>
              </Card.Header>
              <Card.Body>
                <Row>
                  {questions.q1 && (
                    <Col md={4} className="mb-3">
                      <Button
                        variant="outline-primary"
                        className="w-100 h-100 py-3"
                        onClick={() => qCalled("q1")}
                        disabled={isLoading}
                      >
                        {questions.q1}
                      </Button>
                    </Col>
                  )}
                  {questions.q2 && (
                    <Col md={4} className="mb-3">
                      <Button
                        variant="outline-primary"
                        className="w-100 h-100 py-3"
                        onClick={() => qCalled("q2")}
                        disabled={isLoading}
                      >
                        {questions.q2}
                      </Button>
                    </Col>
                  )}
                  {questions.q3 && (
                    <Col md={4} className="mb-3">
                      <Button
                        variant="outline-primary"
                        className="w-100 h-100 py-3"
                        onClick={() => qCalled("q3")}
                        disabled={isLoading}
                      >
                        {questions.q3}
                      </Button>
                    </Col>
                  )}
                </Row>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      )}
    </Container>
  );
}

export default Book;
