"use client";

import { useState } from "react";
import axios from "axios";
import { TypeAnimation } from "react-type-animation";
import { RefreshCw, Search } from "lucide-react";
import { Button, Form, Container, Row, Col, Spinner } from "react-bootstrap";

function Book() {
  const [bookName, setBookName] = useState("");
  const [bookInfo, setBookInfo] = useState("");
  const [bookUrl, setBookUrl] = useState("");
  const [animationKey, setAnimationKey] = useState(0);
  const [questions, setQuestions] = useState({});
  const [showQuestion, setShowQuestion] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [bookMeta, setBookMeta] = useState({ title: "", author: "" });

  const qCalled = async (qKey) => {
    try {
      setIsLoading(true);
      setError("");

      const questionRes = await axios.get(`http://127.0.0.1:5000/question/`);
      const questionText = questionRes.data.q;

      const ansRes = await axios.post(`http://127.0.0.1:5000/questions/`, {
        q: questionText,
      });

      setQuestions((prev) => ({ ...prev, [qKey]: questionText }));
      setBookInfo(ansRes.data.data);
      setShowQuestion(questionText);
      setAnimationKey((prev) => prev + 1);
    } catch (error) {
      console.error("Error fetching question or answer:", error);
      setError("Failed to fetch question or answer. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!bookName.trim()) return;

    try {
      setIsLoading(true);
      setError("");
      setBookInfo("");
      setBookUrl("");

      const response = await axios.get(
        `http://127.0.0.1:5000/info/${bookName}`
      );
      const bookmeta = await axios.get(`http://127.0.0.1:5000/bookinfo`);
      setBookMeta(bookmeta.data);

      const cover_img = await axios.get(
        `https://bookcover.longitood.com/bookcover`,
        {
          params: {
            book_title: bookmeta.data.title,
            author_name: bookmeta.data.author,
          },
        }
      );

      const questionData = await axios.get(`http://127.0.0.1:5000/questions/`);

      // Summarize the full text
      const summaryRes = await axios.post(`http://127.0.0.1:5000/summarize`, {
        text: response.data.data,
      });

      setQuestions(questionData.data);
      setBookUrl(cover_img.data.url);
      setBookInfo(summaryRes.data.summary);
      setShowQuestion("");
      setAnimationKey((prev) => prev + 1);
    } catch (error) {
      console.error("Error fetching book data:", error);
      setError(
        "Failed to fetch book information. Please check the book name and try again."
      );
    } finally {
      setIsLoading(false);
    }
  };

  const handleResummarize = async () => {
    try {
      setIsLoading(true);
      setError("");

      const res = await axios.post(`http://127.0.0.1:5000/summarize`, {
        text: bookInfo,
      });
      setBookInfo(res.data.summary);
      setShowQuestion("Re-summarized:");
      setAnimationKey((prev) => prev + 1);
    } catch (err) {
      console.error("Error re-summarizing:", err);
      setError("Failed to re-summarize. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div
      className="book-page py-5"
      style={{
        backgroundColor: "#0a1f1c",
        minHeight: "100vh",
        color: "#e9e2d0",
      }}
    >
      <Container>
        <div className="text-center mb-5">
          <h1
            className="font-heading mb-3"
            style={{
              color: "#cdaf73",
              fontWeight: "300",
              letterSpacing: "0.2em",
              fontSize: "2.5rem",
            }}
          >
            Discover Literary Wisdom
          </h1>
          <div className="decorative-line mx-auto mb-4"></div>
          <p
            className="mx-auto"
            style={{
              maxWidth: "600px",
              color: "#a8a090",
              letterSpacing: "0.05em",
              fontSize: "1.1rem",
              fontWeight: "300",
            }}
          >
            Explore the essence of literature through AI-powered summaries and
            insights
          </p>
        </div>

        <Row className="justify-content-center mb-5">
          <Col md={8} lg={6}>
            <div
              className="search-container p-4"
              style={{
                border: "1px solid rgba(205, 175, 115, 0.2)",
                borderRadius: "0",
                background: "rgba(10, 31, 28, 0.5)",
              }}
            >
              <Form onSubmit={handleSubmit}>
                <div className="position-relative">
                  <Form.Control
                    type="text"
                    value={bookName}
                    onChange={(event) => setBookName(event.target.value)}
                    placeholder="Enter a book title..."
                    className="py-3 ps-4 pe-4 border-0"
                    style={{
                      backgroundColor: "rgba(0,0,0,0.2)",
                      color: "#e9e2d0",
                      borderRadius: "0",
                      fontSize: "1rem",
                      letterSpacing: "0.05em",
                      fontFamily: "var(--font-lora)",
                    }}
                  />
                  <Button
                    type="submit"
                    className="position-absolute d-flex align-items-center justify-content-center"
                    style={{
                      right: "10px",
                      top: "50%",
                      transform: "translateY(-50%)",
                      backgroundColor: "transparent",
                      border: "none",
                      color: "#cdaf73",
                    }}
                    disabled={isLoading || !bookName.trim()}
                  >
                    {isLoading ? (
                      <Spinner size="sm" animation="border" />
                    ) : (
                      <Search size={18} />
                    )}
                  </Button>
                </div>
              </Form>
            </div>
          </Col>
        </Row>

        {error && (
          <Row className="justify-content-center mb-4">
            <Col md={10}>
              <div
                className="p-3"
                style={{
                  backgroundColor: "rgba(169, 68, 66, 0.1)",
                  color: "#e9a79c",
                  border: "1px solid rgba(169, 68, 66, 0.2)",
                }}
              >
                {error}
              </div>
            </Col>
          </Row>
        )}

        {bookUrl && bookInfo && (
          <Row>
            <Col md={4} lg={3} className="mb-4">
              <div
                className="book-container"
                style={{
                  border: "1px solid rgba(205, 175, 115, 0.2)",
                  padding: "1.5rem",
                  background: "rgba(10, 31, 28, 0.5)",
                }}
              >
                <div
                  className="book-cover-container mb-4"
                  style={{
                    position: "relative",
                    overflow: "hidden",
                    maxWidth: "200px",
                    margin: "0 auto",
                  }}
                >
                  <div className="arch-frame">
                    <img
                      src={bookUrl || "/placeholder.svg"}
                      alt={bookMeta.title}
                      className="img-fluid"
                      style={{
                        width: "100%",
                        height: "auto",
                        objectFit: "cover",
                      }}
                    />
                  </div>
                </div>

                <h4
                  className="font-heading text-center mb-2"
                  style={{
                    color: "#cdaf73",
                    fontWeight: "400",
                    letterSpacing: "0.1em",
                  }}
                >
                  {bookMeta.title}
                </h4>

                {bookMeta.author && (
                  <p
                    className="text-center mb-4"
                    style={{
                      color: "#a8a090",
                      letterSpacing: "0.05em",
                      fontSize: "0.9rem",
                    }}
                  >
                    by {bookMeta.author}
                  </p>
                )}

                <div className="decorative-line mx-auto my-4"></div>

                <div className="d-flex justify-content-center">
                  <Button
                    variant="outline-light"
                    size="sm"
                    className="mx-2 px-3 py-2"
                    style={{
                      fontSize: "0.8rem",
                      borderColor: "rgba(205, 175, 115, 0.3)",
                      color: "#cdaf73",
                      borderRadius: "0",
                      letterSpacing: "0.1em",
                      fontWeight: "300",
                    }}
                  >
                    Add to Library
                  </Button>
                </div>
              </div>
            </Col>

            <Col md={8} lg={9}>
              <div
                className="summary-container mb-4"
                style={{
                  border: "1px solid rgba(205, 175, 115, 0.2)",
                  padding: "2rem",
                  background: "rgba(10, 31, 28, 0.5)",
                }}
              >
                <div className="d-flex justify-content-between align-items-center mb-4">
                  <h3
                    className="font-heading mb-0"
                    style={{
                      color: "#cdaf73",
                      fontWeight: "400",
                      letterSpacing: "0.1em",
                    }}
                  >
                    {showQuestion ? showQuestion : "Book Summary"}
                  </h3>

                  <Button
                    variant="link"
                    className="text-decoration-none p-2 d-flex align-items-center"
                    onClick={handleResummarize}
                    disabled={isLoading}
                    style={{ color: "#cdaf73" }}
                  >
                    <RefreshCw size={16} className="me-2" />
                    <span
                      className="d-none d-md-inline"
                      style={{ letterSpacing: "0.05em", fontWeight: "300" }}
                    >
                      Regenerate
                    </span>
                  </Button>
                </div>

                <div className="decorative-line mb-4"></div>

                {isLoading ? (
                  <div className="text-center py-5">
                    <div className="spinner-container">
                      <Spinner
                        animation="border"
                        style={{ color: "#cdaf73" }}
                      />
                    </div>
                    <p
                      style={{
                        color: "#a8a090",
                        marginTop: "1rem",
                        letterSpacing: "0.05em",
                      }}
                    >
                      Generating insights...
                    </p>
                  </div>
                ) : (
                  <div key={animationKey} className="book-summary">
                    <TypeAnimation
                      sequence={[bookInfo, 3000]}
                      speed={{ type: "keyStrokeDelayInMs", value: 10 }}
                      omitDeletionAnimation={true}
                      style={{
                        whiteSpace: "pre-line",
                        fontSize: "1.1rem",
                        display: "block",
                        minHeight: "200px",
                        lineHeight: "1.8",
                        fontFamily: "var(--font-lora)",
                        color: "#e9e2d0",
                        letterSpacing: "0.02em",
                      }}
                      cursor={false}
                    />
                  </div>
                )}
              </div>

              <div
                className="questions-container"
                style={{
                  border: "1px solid rgba(205, 175, 115, 0.2)",
                  padding: "2rem",
                  background: "rgba(10, 31, 28, 0.5)",
                }}
              >
                <h3
                  className="font-heading mb-4"
                  style={{
                    color: "#cdaf73",
                    fontWeight: "400",
                    letterSpacing: "0.1em",
                  }}
                >
                  Explore Deeper Insights
                </h3>

                <div className="decorative-line mb-4"></div>

                <Row className="g-4 mb-4">
                  {questions.q1 && (
                    <Col md={4}>
                      <div className="question-circle">
                        <Button
                          onClick={() => qCalled("q1")}
                          disabled={isLoading}
                          style={{
                            backgroundColor: "transparent",
                            border: "1px solid rgba(205, 175, 115, 0.3)",
                            color: "#cdaf73",
                            borderRadius: "0",
                            padding: "1.5rem",
                            height: "100%",
                            width: "100%",
                            textAlign: "center",
                            fontFamily: "var(--font-cormorant)",
                            letterSpacing: "0.05em",
                            fontWeight: "300",
                            fontSize: "1rem",
                          }}
                        >
                          {questions.q1}
                        </Button>
                      </div>
                    </Col>
                  )}

                  {questions.q2 && (
                    <Col md={4}>
                      <div className="question-circle">
                        <Button
                          onClick={() => qCalled("q2")}
                          disabled={isLoading}
                          style={{
                            backgroundColor: "transparent",
                            border: "1px solid rgba(205, 175, 115, 0.3)",
                            color: "#cdaf73",
                            borderRadius: "0",
                            padding: "1.5rem",
                            height: "100%",
                            width: "100%",
                            textAlign: "center",
                            fontFamily: "var(--font-cormorant)",
                            letterSpacing: "0.05em",
                            fontWeight: "300",
                            fontSize: "1rem",
                          }}
                        >
                          {questions.q2}
                        </Button>
                      </div>
                    </Col>
                  )}

                  {questions.q3 && (
                    <Col md={4}>
                      <div className="question-circle">
                        <Button
                          onClick={() => qCalled("q3")}
                          disabled={isLoading}
                          style={{
                            backgroundColor: "transparent",
                            border: "1px solid rgba(205, 175, 115, 0.3)",
                            color: "#cdaf73",
                            borderRadius: "0",
                            padding: "1.5rem",
                            height: "100%",
                            width: "100%",
                            textAlign: "center",
                            fontFamily: "var(--font-cormorant)",
                            letterSpacing: "0.05em",
                            fontWeight: "300",
                            fontSize: "1rem",
                          }}
                        >
                          {questions.q3}
                        </Button>
                      </div>
                    </Col>
                  )}
                </Row>

                <div className="custom-question mt-4">
                  <Form.Control
                    type="text"
                    placeholder="Ask your own question about this book..."
                    className="py-3 ps-4 pe-4 border-0"
                    style={{
                      backgroundColor: "rgba(0,0,0,0.2)",
                      color: "#e9e2d0",
                      borderRadius: "0",
                      fontSize: "1rem",
                      letterSpacing: "0.05em",
                      fontFamily: "var(--font-lora)",
                    }}
                  />
                  <Button
                    className="mt-3 px-4 py-2"
                    style={{
                      backgroundColor: "transparent",
                      border: "1px solid rgba(205, 175, 115, 0.3)",
                      color: "#cdaf73",
                      borderRadius: "0",
                      letterSpacing: "0.1em",
                      fontWeight: "300",
                    }}
                  >
                    Ask Question
                  </Button>
                </div>
              </div>
            </Col>
          </Row>
        )}
      </Container>
    </div>
  );
}

export default Book;
